import React from 'react';

const CoursesSection = () => {
  const courses = {
    youtube: {
      available: [
        { title: 'React Masterclass', instructor: 'John Doe', duration: '12 hours', level: 'Advanced' },
        { title: 'JavaScript Fundamentals', instructor: 'Jane Smith', duration: '8 hours', level: 'Beginner' },
        { title: 'UI/UX Design Principles', instructor: 'Mike Johnson', duration: '6 hours', level: 'Intermediate' }
      ],
      recommended: [
        { title: 'Advanced React Patterns', instructor: 'Sarah Wilson', duration: '10 hours', level: 'Advanced' },
        { title: 'CSS Grid & Flexbox', instructor: 'Tom Brown', duration: '5 hours', level: 'Intermediate' },
        { title: 'Web Performance', instructor: 'Lisa Chen', duration: '7 hours', level: 'Advanced' }
      ]
    },
    coursera: {
      available: [
        { title: 'Machine Learning Specialization', instructor: 'Andrew Ng', duration: '3 months', level: 'Intermediate' },
        { title: 'Data Science Fundamentals', instructor: 'Dr. Emily White', duration: '2 months', level: 'Beginner' },
        { title: 'Cloud Computing Basics', instructor: 'Prof. David Lee', duration: '1 month', level: 'Beginner' }
      ],
      recommended: [
        { title: 'AI for Everyone', instructor: 'Andrew Ng', duration: '1 month', level: 'Beginner' },
        { title: 'Deep Learning Specialization', instructor: 'Dr. Alex Johnson', duration: '4 months', level: 'Advanced' },
        { title: 'Blockchain Fundamentals', instructor: 'Prof. Maria Garcia', duration: '2 months', level: 'Intermediate' }
      ]
    }
  };

  const CourseCard = ({ course, platform, index }) => (
    <div 
      className="card holographic"
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div style={{
        display: 'flex',
        alignItems: 'center',
        marginBottom: '1rem'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          background: platform === 'youtube' ? '#FF0000' : '#0056D2',
          borderRadius: '8px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginRight: '1rem',
          fontWeight: '600',
          color: 'white',
          fontSize: '0.8rem'
        }}>
          {platform === 'youtube' ? 'YT' : 'CS'}
        </div>
        <div>
          <h4 style={{ color: 'white', marginBottom: '0.25rem' }}>{course.title}</h4>
          <p style={{ color: '#00FFFF', fontSize: '0.9rem' }}>{course.instructor}</p>
        </div>
      </div>
      
      <div style={{ 
        display: 'flex',
        justifyContent: 'space-between',
        color: '#00BFFF', 
        fontSize: '0.8rem',
        marginBottom: '1rem'
      }}>
        <span>{course.duration}</span>
        <span className="badge">{course.level}</span>
      </div>
      
      <button className="btn-secondary" style={{ width: '100%' }}>
        View Course
      </button>
    </div>
  );

  const CourseGrid = ({ title, courses, platform }) => (
    <div style={{ marginBottom: '3rem' }}>
      <h3 style={{
        fontSize: '1.5rem',
        color: '#00FFFF',
        marginBottom: '1.5rem'
      }}>
        {title}
      </h3>
      <div className="grid grid-cols-3">
        {courses.map((course, index) => (
          <CourseCard 
            key={course.title} 
            course={course} 
            platform={platform}
            index={index}
          />
        ))}
      </div>
    </div>
  );

  return (
    <section className="section">
      <div className="container">
        <h2 className="section-title fade-in-up">
          Learning Courses
        </h2>
        
        <div style={{ marginBottom: '4rem' }}>
          <h3 style={{
            fontSize: '2rem',
            color: '#00BFFF',
            marginBottom: '2rem',
            textAlign: 'center'
          }}>
            YouTube Courses
          </h3>
          <CourseGrid 
            title="Available Courses (YouTube)"
            courses={courses.youtube.available}
            platform="youtube"
          />
          <CourseGrid 
            title="Recommended Courses (YouTube)"
            courses={courses.youtube.recommended}
            platform="youtube"
          />
        </div>
        
        <div>
          <h3 style={{
            fontSize: '2rem',
            color: '#00BFFF',
            marginBottom: '2rem',
            textAlign: 'center'
          }}>
            Coursera Courses
          </h3>
          <CourseGrid 
            title="Available Courses (Coursera)"
            courses={courses.coursera.available}
            platform="coursera"
          />
          <CourseGrid 
            title="Recommended Courses (Coursera)"
            courses={courses.coursera.recommended}
            platform="coursera"
          />
        </div>
      </div>
    </section>
  );
};

export default CoursesSection;
