import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();
  const languageRef = useRef(null);

  const languages = [
    { code: 'en', name: 'English', flag: 'üá∫üá∏' },
    { code: 'hi', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä', flag: 'üáÆüá≥' },
    { code: 'or', name: '‡¨ì‡¨°‡¨º‡¨ø‡¨Ü', flag: 'üáÆüá≥' }
  ];

  // Debug logging
  console.log('Header - Current Language:', currentLanguage);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    console.log('Changing language to:', language);
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  // Close language dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageRef.current && !languageRef.current.contains(event.target)) {
        setIsLanguageOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">üîç</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">
              <span className="nav-icon">üíº</span>
              {t('header.opportunities')}
            </Link>
            <Link to="/mentors" className="nav-item">
              <span className="nav-icon">üë®‚Äçüè´</span>
              {t('header.mentors')}
            </Link>
            <Link to="/games" className="nav-item">
              <span className="nav-icon">üéÆ</span>
              {t('header.games')}
            </Link>
            <Link to="/badges" className="nav-item">
              <span className="nav-icon">üèÜ</span>
              {t('header.badges')}
            </Link>
            <Link to="/aipro" className="nav-item ai-pro">
              <span className="nav-icon">ü§ñ</span>
              {t('header.aipro')}
            </Link>
            
            <div 
              className="language-selector" 
              ref={languageRef}
              style={{
                position: 'relative',
                display: 'inline-block',
                zIndex: '1002'
              }}
            >
              <button 
                onClick={toggleLanguage} 
                className="language-btn"
                style={{
                  background: 'rgba(255,255,255,0.1)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.2)',
                  padding: '0.5rem 1rem',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.9rem',
                  outline: 'none',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = 'rgba(255,255,255,0.2)';
                  e.target.style.transform = 'translateY(-2px)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = 'rgba(255,255,255,0.1)';
                  e.target.style.transform = 'translateY(0)';
                }}
              >
                üåê {languages.find(lang => lang.code === currentLanguage)?.name || 'English'}
              </button>
              {isLanguageOpen && (
                <div 
                  className="language-dropdown"
                  style={{
                    position: 'absolute',
                    top: '100%',
                    right: '0',
                    background: 'white',
                    borderRadius: '12px',
                    boxShadow: '0 15px 35px rgba(0,0,0,0.25)',
                    padding: '0.5rem 0',
                    minWidth: '160px',
                    zIndex: '1003',
                    marginTop: '0.5rem',
                    border: '1px solid rgba(0,0,0,0.1)',
                    overflow: 'hidden'
                  }}
                >
                  {languages.map((language) => (
                    <button
                      key={language.code}
                      onClick={() => {
                        console.log('Language clicked:', language.code);
                        handleLanguageChange(language.code);
                      }}
                      className={`language-option ${currentLanguage === language.code ? 'active' : ''}`}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.75rem',
                        padding: '0.75rem 1rem',
                        border: 'none',
                        background: currentLanguage === language.code ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : 'transparent',
                        color: currentLanguage === language.code ? 'white' : '#333',
                        cursor: 'pointer',
                        width: '100%',
                        textAlign: 'left',
                        fontSize: '0.9rem',
                        transition: 'all 0.2s ease'
                      }}
                      onMouseEnter={(e) => {
                        if (currentLanguage !== language.code) {
                          e.target.style.background = 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
                          e.target.style.color = 'white';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (currentLanguage !== language.code) {
                          e.target.style.background = 'transparent';
                          e.target.style.color = '#333';
                        }
                      }}
                    >
                      <span className="flag">{language.flag}</span>
                      <span className="name">{language.name}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
            

            <button onClick={toggleProfile} className="profile-btn">üë§ {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
