import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();
  const languageRef = useRef(null);

  const languages = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' }
  ];

  // Debug logging
  console.log('Header - Current Language:', currentLanguage);
  console.log('Header - isLanguageOpen:', isLanguageOpen);
  console.log('Header - Available languages:', languages);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    console.log('Toggle language dropdown. Current state:', isLanguageOpen, 'New state:', !isLanguageOpen);
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    console.log('Changing language to:', language);
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  // Close language dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageRef.current && !languageRef.current.contains(event.target)) {
        setIsLanguageOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">🔍</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">
              <span className="nav-icon">💼</span>
              {t('header.opportunities')}
            </Link>
            <Link to="/mentors" className="nav-item">
              <span className="nav-icon">👨‍🏫</span>
              {t('header.mentors')}
            </Link>
            <Link to="/games" className="nav-item">
              <span className="nav-icon">🎮</span>
              {t('header.games')}
            </Link>
            <Link to="/badges" className="nav-item">
              <span className="nav-icon">🏆</span>
              {t('header.badges')}
            </Link>
            <Link to="/aipro" className="nav-item ai-pro">
              <span className="nav-icon">🤖</span>
              {t('header.aipro')}
            </Link>
            
            {/* Language Selector */}
            <div ref={languageRef} style={{ position: 'relative', display: 'inline-block' }}>
              <button 
                onClick={toggleLanguage}
                style={{
                  background: 'rgba(255,255,255,0.1)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.2)',
                  padding: '8px 16px',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '500',
                  outline: 'none'
                }}
              >
                🌐 {languages.find(lang => lang.code === currentLanguage)?.name || 'English'} ▼
              </button>
              
              {isLanguageOpen && (
                <div style={{
                  position: 'absolute',
                  top: '100%',
                  right: '0',
                  background: '#ffffff',
                  border: '2px solid #333333',
                  borderRadius: '8px',
                  boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
                  marginTop: '8px',
                  minWidth: '180px',
                  zIndex: '99999',
                  overflow: 'hidden'
                }}>
                  <div 
                    onClick={() => {
                      console.log('English clicked');
                      handleLanguageChange('en');
                    }}
                    style={{
                      padding: '15px 20px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #dddddd',
                      background: currentLanguage === 'en' ? '#667eea' : '#ffffff',
                      color: currentLanguage === 'en' ? '#ffffff !important' : '#000000 !important',
                      fontSize: '16px',
                      fontWeight: '500',
                      fontFamily: 'Arial, sans-serif',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      transition: 'all 0.2s ease'
                    }}
                    onMouseEnter={(e) => {
                      if (currentLanguage !== 'en') {
                        e.target.style.background = '#f0f0f0';
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (currentLanguage !== 'en') {
                        e.target.style.background = '#ffffff';
                      }
                    }}
                  >
                    <span style={{ fontSize: '20px' }}>🇺🇸</span>
                    <span style={{ color: 'inherit', fontWeight: 'inherit' }}>English</span>
                  </div>
                  <div 
                    onClick={() => {
                      console.log('Hindi clicked');
                      handleLanguageChange('hi');
                    }}
                    style={{
                      padding: '15px 20px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #dddddd',
                      background: currentLanguage === 'hi' ? '#667eea' : '#ffffff',
                      color: currentLanguage === 'hi' ? '#ffffff !important' : '#000000 !important',
                      fontSize: '16px',
                      fontWeight: '500',
                      fontFamily: 'Arial, sans-serif',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      transition: 'all 0.2s ease'
                    }}
                    onMouseEnter={(e) => {
                      if (currentLanguage !== 'hi') {
                        e.target.style.background = '#f0f0f0';
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (currentLanguage !== 'hi') {
                        e.target.style.background = '#ffffff';
                      }
                    }}
                  >
                    <span style={{ fontSize: '20px' }}>🇮🇳</span>
                    <span style={{ color: 'inherit', fontWeight: 'inherit' }}>हिंदी</span>
                  </div>
                  <div 
                    onClick={() => {
                      console.log('Odia clicked');
                      handleLanguageChange('or');
                    }}
                    style={{
                      padding: '15px 20px',
                      cursor: 'pointer',
                      background: currentLanguage === 'or' ? '#667eea' : '#ffffff',
                      color: currentLanguage === 'or' ? '#ffffff !important' : '#000000 !important',
                      fontSize: '16px',
                      fontWeight: '500',
                      fontFamily: 'Arial, sans-serif',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      transition: 'all 0.2s ease'
                    }}
                    onMouseEnter={(e) => {
                      if (currentLanguage !== 'or') {
                        e.target.style.background = '#f0f0f0';
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (currentLanguage !== 'or') {
                        e.target.style.background = '#ffffff';
                      }
                    }}
                  >
                    <span style={{ fontSize: '20px' }}>🇮🇳</span>
                    <span style={{ color: 'inherit', fontWeight: 'inherit' }}>ଓଡ଼ିଆ</span>
                  </div>
                </div>
              )}
            </div>
            


            
            <button onClick={toggleProfile} className="profile-btn">👤 {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
