import React, { useState } from 'react';
import '../styles/profileEdit.css';

function ProfileEdit() {
  console.log('ProfileEdit component loaded successfully!');
  const [activeTab, setActiveTab] = useState('personal');
  const [profileData, setProfileData] = useState({
    // Personal Information
    firstName: 'Arjun',
    lastName: 'Sharma',
    email: 'arjun.sharma@student.edu',
    phone: '+91 9876543210',
    dateOfBirth: '1999-05-15',
    gender: 'Male',
    location: 'Bangalore, India',
    bio: 'Passionate computer science student with expertise in full-stack development and AI/ML.',
    
    // Education
    currentEducation: {
      institution: 'Indian Institute of Technology',
      degree: 'Bachelor of Technology',
      field: 'Computer Science Engineering',
      startYear: '2021',
      endYear: '2025',
      cgpa: '8.7',
      semester: '7th Semester'
    },
    previousEducation: [
      {
        institution: 'Delhi Public School',
        degree: '12th Grade',
        field: 'Science (PCM)',
        year: '2021',
        percentage: '94.5%'
      }
    ],
    
    // Skills
    technicalSkills: ['React', 'Node.js', 'Python', 'JavaScript', 'MongoDB', 'AWS', 'Docker', 'Git'],
    softSkills: ['Leadership', 'Communication', 'Problem Solving', 'Team Work'],
    
    // Experience
    workExperience: [
      {
        company: 'TechCorp Solutions',
        position: 'Software Development Intern',
        startDate: '2024-06',
        endDate: '2024-08',
        description: 'Developed web applications using React and Node.js'
      }
    ],
    
    // Projects
    projects: [
      {
        name: 'E-commerce Platform',
        description: 'Full-stack e-commerce application with payment integration',
        technologies: ['React', 'Node.js', 'MongoDB'],
        link: 'https://github.com/arjun/ecommerce'
      }
    ],
    
    // Achievements
    achievements: [
      {
        title: 'Winner - National Coding Championship',
        date: '2024-03',
        description: 'Secured 1st position among 5000+ participants'
      }
    ],
    
    // Social Links
    socialLinks: {
      linkedin: 'https://linkedin.com/in/arjunsharma',
      github: 'https://github.com/arjunsharma',
      portfolio: 'https://arjunsharma.dev',
      twitter: 'https://twitter.com/arjunsharma'
    }
  });

  const [profileImage, setProfileImage] = useState('/profile-avatar.svg');
  const [backgroundImage, setBackgroundImage] = useState('');

  const handleInputChange = (section, field, value, index = null) => {
    setProfileData(prev => {
      if (index !== null) {
        const newArray = [...prev[section]];
        newArray[index] = { ...newArray[index], [field]: value };
        return { ...prev, [section]: newArray };
      } else if (section === 'currentEducation' || section === 'socialLinks') {
        return {
          ...prev,
          [section]: { ...prev[section], [field]: value }
        };
      } else {
        return { ...prev, [field]: value };
      }
    });
  };

  const addArrayItem = (section, newItem) => {
    setProfileData(prev => ({
      ...prev,
      [section]: [...prev[section], newItem]
    }));
  };

  const removeArrayItem = (section, index) => {
    setProfileData(prev => ({
      ...prev,
      [section]: prev[section].filter((_, i) => i !== index)
    }));
  };

  const handleImageUpload = (type, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (type === 'profile') {
          setProfileImage(e.target.result);
        } else {
          setBackgroundImage(e.target.result);
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const renderPersonalInfo = () => (
    <div className="edit-section">
      <h3 className="section-title">
        <span className="section-icon">👤</span>
        Personal Information
      </h3>
      
      <div className="form-grid">
        <div className="form-group">
          <label>First Name</label>
          <input
            type="text"
            value={profileData.firstName}
            onChange={(e) => handleInputChange(null, 'firstName', e.target.value)}
            className="form-input"
          />
        </div>
        
        <div className="form-group">
          <label>Last Name</label>
          <input
            type="text"
            value={profileData.lastName}
            onChange={(e) => handleInputChange(null, 'lastName', e.target.value)}
            className="form-input"
          />
        </div>
        
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={profileData.email}
            onChange={(e) => handleInputChange(null, 'email', e.target.value)}
            className="form-input"
          />
        </div>
        
        <div className="form-group">
          <label>Phone</label>
          <input
            type="tel"
            value={profileData.phone}
            onChange={(e) => handleInputChange(null, 'phone', e.target.value)}
            className="form-input"
          />
        </div>
        
        <div className="form-group">
          <label>Date of Birth</label>
          <input
            type="date"
            value={profileData.dateOfBirth}
            onChange={(e) => handleInputChange(null, 'dateOfBirth', e.target.value)}
            className="form-input"
          />
        </div>
        
        <div className="form-group">
          <label>Gender</label>
          <select
            value={profileData.gender}
            onChange={(e) => handleInputChange(null, 'gender', e.target.value)}
            className="form-input"
          >
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
            <option value="Prefer not to say">Prefer not to say</option>
          </select>
        </div>
        
        <div className="form-group full-width">
          <label>Location</label>
          <input
            type="text"
            value={profileData.location}
            onChange={(e) => handleInputChange(null, 'location', e.target.value)}
            className="form-input"
            placeholder="City, State, Country"
          />
        </div>
        
        <div className="form-group full-width">
          <label>Bio</label>
          <textarea
            value={profileData.bio}
            onChange={(e) => handleInputChange(null, 'bio', e.target.value)}
            className="form-textarea"
            rows="4"
            placeholder="Tell us about yourself..."
          />
        </div>
      </div>
    </div>
  );

  const renderEducation = () => (
    <div className="edit-section">
      <h3 className="section-title">
        <span className="section-icon">🎓</span>
        Education Details
      </h3>
      
      {/* Current Education */}
      <div className="education-card">
        <h4 className="card-title">Current Education</h4>
        <div className="form-grid">
          <div className="form-group">
            <label>Institution</label>
            <input
              type="text"
              value={profileData.currentEducation.institution}
              onChange={(e) => handleInputChange('currentEducation', 'institution', e.target.value)}
              className="form-input"
            />
          </div>
          
          <div className="form-group">
            <label>Degree</label>
            <input
              type="text"
              value={profileData.currentEducation.degree}
              onChange={(e) => handleInputChange('currentEducation', 'degree', e.target.value)}
              className="form-input"
            />
          </div>
          
          <div className="form-group">
            <label>Field of Study</label>
            <input
              type="text"
              value={profileData.currentEducation.field}
              onChange={(e) => handleInputChange('currentEducation', 'field', e.target.value)}
              className="form-input"
            />
          </div>
          
          <div className="form-group">
            <label>Current Semester</label>
            <select
              value={profileData.currentEducation.semester}
              onChange={(e) => handleInputChange('currentEducation', 'semester', e.target.value)}
              className="form-input"
            >
              {[1,2,3,4,5,6,7,8].map(sem => (
                <option key={sem} value={`${sem}th Semester`}>{sem}th Semester</option>
              ))}
            </select>
          </div>
          
          <div className="form-group">
            <label>Current CGPA</label>
            <input
              type="number"
              step="0.01"
              min="0"
              max="10"
              value={profileData.currentEducation.cgpa}
              onChange={(e) => handleInputChange('currentEducation', 'cgpa', e.target.value)}
              className="form-input"
            />
          </div>
          
          <div className="form-group">
            <label>Start Year</label>
            <input
              type="number"
              value={profileData.currentEducation.startYear}
              onChange={(e) => handleInputChange('currentEducation', 'startYear', e.target.value)}
              className="form-input"
            />
          </div>
          
          <div className="form-group">
            <label>Expected End Year</label>
            <input
              type="number"
              value={profileData.currentEducation.endYear}
              onChange={(e) => handleInputChange('currentEducation', 'endYear', e.target.value)}
              className="form-input"
            />
          </div>
        </div>
      </div>
      
      {/* Previous Education */}
      <div className="education-card">
        <div className="card-header">
          <h4 className="card-title">Previous Education</h4>
          <button
            className="add-btn"
            onClick={() => addArrayItem('previousEducation', {
              institution: '',
              degree: '',
              field: '',
              year: '',
              percentage: ''
            })}
          >
            + Add Education
          </button>
        </div>
        
        {profileData.previousEducation.map((edu, index) => (
          <div key={index} className="education-item">
            <div className="form-grid">
              <div className="form-group">
                <label>Institution</label>
                <input
                  type="text"
                  value={edu.institution}
                  onChange={(e) => handleInputChange('previousEducation', 'institution', e.target.value, index)}
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label>Degree/Class</label>
                <input
                  type="text"
                  value={edu.degree}
                  onChange={(e) => handleInputChange('previousEducation', 'degree', e.target.value, index)}
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label>Field/Stream</label>
                <input
                  type="text"
                  value={edu.field}
                  onChange={(e) => handleInputChange('previousEducation', 'field', e.target.value, index)}
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label>Year</label>
                <input
                  type="number"
                  value={edu.year}
                  onChange={(e) => handleInputChange('previousEducation', 'year', e.target.value, index)}
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label>Percentage/CGPA</label>
                <input
                  type="text"
                  value={edu.percentage}
                  onChange={(e) => handleInputChange('previousEducation', 'percentage', e.target.value, index)}
                  className="form-input"
                />
              </div>
            </div>
            
            <button
              className="remove-btn"
              onClick={() => removeArrayItem('previousEducation', index)}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  const renderSkills = () => (
    <div className="edit-section">
      <h3 className="section-title">
        <span className="section-icon">💡</span>
        Skills & Expertise
      </h3>
      
      <div className="skills-section">
        <h4 className="card-title">Technical Skills</h4>
        <div className="skills-input">
          <input
            type="text"
            placeholder="Add a technical skill and press Enter"
            className="form-input"
            onKeyPress={(e) => {
              if (e.key === 'Enter' && e.target.value.trim()) {
                setProfileData(prev => ({
                  ...prev,
                  technicalSkills: [...prev.technicalSkills, e.target.value.trim()]
                }));
                e.target.value = '';
              }
            }}
          />
        </div>
        <div className="skills-list">
          {profileData.technicalSkills.map((skill, index) => (
            <span key={index} className="skill-tag">
              {skill}
              <button
                className="skill-remove"
                onClick={() => {
                  setProfileData(prev => ({
                    ...prev,
                    technicalSkills: prev.technicalSkills.filter((_, i) => i !== index)
                  }));
                }}
              >
                ×
              </button>
            </span>
          ))}
        </div>
      </div>
      
      <div className="skills-section">
        <h4 className="card-title">Soft Skills</h4>
        <div className="skills-input">
          <input
            type="text"
            placeholder="Add a soft skill and press Enter"
            className="form-input"
            onKeyPress={(e) => {
              if (e.key === 'Enter' && e.target.value.trim()) {
                setProfileData(prev => ({
                  ...prev,
                  softSkills: [...prev.softSkills, e.target.value.trim()]
                }));
                e.target.value = '';
              }
            }}
          />
        </div>
        <div className="skills-list">
          {profileData.softSkills.map((skill, index) => (
            <span key={index} className="skill-tag soft-skill">
              {skill}
              <button
                className="skill-remove"
                onClick={() => {
                  setProfileData(prev => ({
                    ...prev,
                    softSkills: prev.softSkills.filter((_, i) => i !== index)
                  }));
                }}
              >
                ×
              </button>
            </span>
          ))}
        </div>
      </div>
    </div>
  );

  const renderExperience = () => (
    <div className="edit-section">
      <h3 className="section-title">
        <span className="section-icon">💼</span>
        Work Experience
      </h3>
      
      <div className="card-header">
        <button
          className="add-btn"
          onClick={() => addArrayItem('workExperience', {
            company: '',
            position: '',
            startDate: '',
            endDate: '',
            description: ''
          })}
        >
          + Add Experience
        </button>
      </div>
      
      {profileData.workExperience.map((exp, index) => (
        <div key={index} className="experience-item">
          <div className="form-grid">
            <div className="form-group">
              <label>Company</label>
              <input
                type="text"
                value={exp.company}
                onChange={(e) => handleInputChange('workExperience', 'company', e.target.value, index)}
                className="form-input"
              />
            </div>
            
            <div className="form-group">
              <label>Position</label>
              <input
                type="text"
                value={exp.position}
                onChange={(e) => handleInputChange('workExperience', 'position', e.target.value, index)}
                className="form-input"
              />
            </div>
            
            <div className="form-group">
              <label>Start Date</label>
              <input
                type="month"
                value={exp.startDate}
                onChange={(e) => handleInputChange('workExperience', 'startDate', e.target.value, index)}
                className="form-input"
              />
            </div>
            
            <div className="form-group">
              <label>End Date</label>
              <input
                type="month"
                value={exp.endDate}
                onChange={(e) => handleInputChange('workExperience', 'endDate', e.target.value, index)}
                className="form-input"
              />
            </div>
            
            <div className="form-group full-width">
              <label>Description</label>
              <textarea
                value={exp.description}
                onChange={(e) => handleInputChange('workExperience', 'description', e.target.value, index)}
                className="form-textarea"
                rows="3"
              />
            </div>
          </div>
          
          <button
            className="remove-btn"
            onClick={() => removeArrayItem('workExperience', index)}
          >
            Remove Experience
          </button>
        </div>
      ))}
    </div>
  );

  const renderSocialLinks = () => (
    <div className="edit-section">
      <h3 className="section-title">
        <span className="section-icon">🔗</span>
        Social Links
      </h3>
      
      <div className="form-grid">
        <div className="form-group">
          <label>LinkedIn</label>
          <input
            type="url"
            value={profileData.socialLinks.linkedin}
            onChange={(e) => handleInputChange('socialLinks', 'linkedin', e.target.value)}
            className="form-input"
            placeholder="https://linkedin.com/in/username"
          />
        </div>
        
        <div className="form-group">
          <label>GitHub</label>
          <input
            type="url"
            value={profileData.socialLinks.github}
            onChange={(e) => handleInputChange('socialLinks', 'github', e.target.value)}
            className="form-input"
            placeholder="https://github.com/username"
          />
        </div>
        
        <div className="form-group">
          <label>Portfolio</label>
          <input
            type="url"
            value={profileData.socialLinks.portfolio}
            onChange={(e) => handleInputChange('socialLinks', 'portfolio', e.target.value)}
            className="form-input"
            placeholder="https://yourportfolio.com"
          />
        </div>
        
        <div className="form-group">
          <label>Twitter</label>
          <input
            type="url"
            value={profileData.socialLinks.twitter}
            onChange={(e) => handleInputChange('socialLinks', 'twitter', e.target.value)}
            className="form-input"
            placeholder="https://twitter.com/username"
          />
        </div>
      </div>
    </div>
  );

  // Simple test to ensure component loads
  if (true) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '2rem',
        color: 'white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '20px',
          padding: '3rem',
          textAlign: 'center',
          maxWidth: '800px'
        }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>
            🎉 Profile Edit Page Loaded Successfully!
          </h1>
          <p style={{ fontSize: '1.2rem', opacity: 0.9, marginBottom: '2rem' }}>
            The "View Profile" button is working correctly. The comprehensive profile editing interface is loading...
          </p>
          <div style={{
            background: 'linear-gradient(45deg, #feca57, #ff9ff3)',
            color: 'white',
            padding: '1rem 2rem',
            borderRadius: '25px',
            display: 'inline-block',
            fontWeight: '600'
          }}>
            Navigation Successful ✅
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-edit-page">
      {/* Background Image */}
      <div 
        className="profile-background"
        style={{
          backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
        }}
      >
        <div className="background-overlay"></div>
        <div className="background-upload">
          <input
            type="file"
            id="background-upload"
            accept="image/*"
            onChange={(e) => handleImageUpload('background', e)}
            style={{ display: 'none' }}
          />
          <label htmlFor="background-upload" className="upload-btn">
            📷 Change Background
          </label>
        </div>
      </div>

      {/* Profile Header */}
      <div className="profile-header">
        <div className="profile-image-container">
          <img src={profileImage} alt="Profile" className="profile-image" />
          <div className="image-upload-overlay">
            <input
              type="file"
              id="profile-upload"
              accept="image/*"
              onChange={(e) => handleImageUpload('profile', e)}
              style={{ display: 'none' }}
            />
            <label htmlFor="profile-upload" className="image-upload-btn">
              📷
            </label>
          </div>
        </div>
        
        <div className="profile-info">
          <h1 className="profile-name">{profileData.firstName} {profileData.lastName}</h1>
          <p className="profile-email">{profileData.email}</p>
          <div className="profile-stats">
            <div className="stat-item">
              <span className="stat-number">2,847</span>
              <span className="stat-label">Profile Views</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{profileData.currentEducation.cgpa}</span>
              <span className="stat-label">CGPA</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{profileData.currentEducation.semester}</span>
              <span className="stat-label">Current</span>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="edit-navigation">
        <div className="nav-tabs">
          {[
            { id: 'personal', label: 'Personal Info', icon: '👤' },
            { id: 'education', label: 'Education', icon: '🎓' },
            { id: 'skills', label: 'Skills', icon: '💡' },
            { id: 'experience', label: 'Experience', icon: '💼' },
            { id: 'social', label: 'Social Links', icon: '🔗' }
          ].map(tab => (
            <button
              key={tab.id}
              className={`nav-tab ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              <span className="tab-icon">{tab.icon}</span>
              <span className="tab-label">{tab.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Content Area */}
      <div className="edit-content">
        <div className="container">
          {activeTab === 'personal' && renderPersonalInfo()}
          {activeTab === 'education' && renderEducation()}
          {activeTab === 'skills' && renderSkills()}
          {activeTab === 'experience' && renderExperience()}
          {activeTab === 'social' && renderSocialLinks()}
          
          {/* Save Button */}
          <div className="save-section">
            <button className="save-btn">
              💾 Save Changes
            </button>
            <button className="preview-btn">
              👁️ Preview Profile
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfileEdit;
