import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();
  const languageRef = useRef(null);

  const languages = [
    { code: 'en', name: 'English', flag: 'üá∫üá∏' },
    { code: 'hi', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä', flag: 'üáÆüá≥' },
    { code: 'or', name: '‡¨ì‡¨°‡¨º‡¨ø‡¨Ü', flag: 'üáÆüá≥' }
  ];

  // Debug logging
  console.log('Header - Current Language:', currentLanguage);
  console.log('Header - isLanguageOpen:', isLanguageOpen);
  console.log('Header - Available languages:', languages);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    console.log('Toggle language dropdown. Current state:', isLanguageOpen, 'New state:', !isLanguageOpen);
    alert(`Language dropdown ${!isLanguageOpen ? 'OPENING' : 'CLOSING'}`);
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    console.log('Changing language to:', language);
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  // Close language dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageRef.current && !languageRef.current.contains(event.target)) {
        setIsLanguageOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">üîç</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">
              <span className="nav-icon">üíº</span>
              {t('header.opportunities')}
            </Link>
            <Link to="/mentors" className="nav-item">
              <span className="nav-icon">üë®‚Äçüè´</span>
              {t('header.mentors')}
            </Link>
            <Link to="/games" className="nav-item">
              <span className="nav-icon">üéÆ</span>
              {t('header.games')}
            </Link>
            <Link to="/badges" className="nav-item">
              <span className="nav-icon">üèÜ</span>
              {t('header.badges')}
            </Link>
            <Link to="/aipro" className="nav-item ai-pro">
              <span className="nav-icon">ü§ñ</span>
              {t('header.aipro')}
            </Link>
            
            {/* Simple Language Selector using HTML Select */}
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <span style={{ color: 'white', fontSize: '20px' }}>üåê</span>
              <select 
                value={currentLanguage}
                onChange={(e) => {
                  console.log('Language changed via select:', e.target.value);
                  handleLanguageChange(e.target.value);
                }}
                style={{
                  background: 'white',
                  color: 'black',
                  border: '2px solid #667eea',
                  borderRadius: '8px',
                  padding: '8px 12px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer',
                  outline: 'none',
                  minWidth: '120px'
                }}
              >
                <option value="en">üá∫üá∏ English</option>
                <option value="hi">üáÆüá≥ ‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
                <option value="or">üáÆüá≥ ‡¨ì‡¨°‡¨º‡¨ø‡¨Ü</option>
              </select>
            </div>

            {/* Alternative Button Method (for testing) */}
            <div style={{ display: 'flex', gap: '5px' }}>
              <button 
                onClick={() => handleLanguageChange('en')}
                style={{
                  background: currentLanguage === 'en' ? '#667eea' : 'rgba(255,255,255,0.2)',
                  color: 'white',
                  border: '1px solid white',
                  padding: '5px 8px',
                  borderRadius: '15px',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                EN
              </button>
              <button 
                onClick={() => handleLanguageChange('hi')}
                style={{
                  background: currentLanguage === 'hi' ? '#667eea' : 'rgba(255,255,255,0.2)',
                  color: 'white',
                  border: '1px solid white',
                  padding: '5px 8px',
                  borderRadius: '15px',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                ‡§π‡§ø
              </button>
              <button 
                onClick={() => handleLanguageChange('or')}
                style={{
                  background: currentLanguage === 'or' ? '#667eea' : 'rgba(255,255,255,0.2)',
                  color: 'white',
                  border: '1px solid white',
                  padding: '5px 8px',
                  borderRadius: '15px',
                  cursor: 'pointer',
                  fontSize: '12px'
                }}
              >
                ‡¨ì‡¨°‡¨º
              </button>
            </div>
            


            
            <button onClick={toggleProfile} className="profile-btn">üë§ {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
