import React from 'react';

const JobsSection = () => {
  const jobs = {
    available: [
      {
        title: 'Frontend Developer',
        company: 'TechCorp',
        location: 'Remote',
        type: 'Full-time',
        description: 'Build amazing user interfaces with modern technologies'
      },
      {
        title: 'UI/UX Designer',
        company: 'DesignStudio',
        location: 'New York',
        type: 'Contract',
        description: 'Create beautiful and intuitive user experiences'
      },
      {
        title: 'Data Scientist',
        company: 'DataInsights',
        location: 'San Francisco',
        type: 'Full-time',
        description: 'Work with big data and machine learning algorithms'
      }
    ],
    recommended: [
      {
        title: 'React Native Developer',
        company: 'MobileFirst',
        location: 'Remote',
        type: 'Full-time',
        description: 'Develop cross-platform mobile applications'
      },
      {
        title: 'Full Stack Developer',
        company: 'WebSolutions',
        location: 'Bangalore',
        type: 'Full-time',
        description: 'End-to-end web application development'
      },
      {
        title: 'AI Engineer',
        company: 'AIInnovations',
        location: 'Austin',
        type: 'Contract',
        description: 'Implement cutting-edge AI solutions'
      }
    ]
  };

  const JobCard = ({ job, index }) => (
    <div 
      className="card holographic"
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div style={{
        display: 'flex',
        alignItems: 'center',
        marginBottom: '1rem'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          background: 'linear-gradient(45deg, #00FFFF, #00BFFF)',
          borderRadius: '8px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginRight: '1rem',
          fontWeight: '600'
        }}>
          {job.company.charAt(0)}
        </div>
        <div>
          <h4 style={{ color: 'white', marginBottom: '0.25rem' }}>{job.title}</h4>
          <p style={{ color: '#00FFFF', fontSize: '0.9rem' }}>{job.company}</p>
        </div>
      </div>
      
      <p style={{ 
        color: '#00BFFF', 
        fontSize: '0.9rem',
        marginBottom: '1rem'
      }}>
        {job.location} â€¢ {job.type}
      </p>
      
      <p style={{ 
        color: 'rgba(255, 255, 255, 0.8)',
        fontSize: '0.9rem',
        marginBottom: '1.5rem',
        lineHeight: '1.4'
      }}>
        {job.description}
      </p>
      
      <button className="btn-primary" style={{ width: '100%' }}>
        Apply Now
      </button>
    </div>
  );

  return (
    <section className="section">
      <div className="container">
        <h2 className="section-title fade-in-up">
          Job Opportunities
        </h2>
        
        <div style={{ marginBottom: '4rem' }}>
          <h3 style={{
            fontSize: '2rem',
            color: '#00FFFF',
            marginBottom: '2rem',
            textAlign: 'center'
          }}>
            Available Jobs
          </h3>
          <div className="grid grid-cols-3">
            {jobs.available.map((job, index) => (
              <JobCard key={job.title} job={job} index={index} />
            ))}
          </div>
        </div>
        
        <div>
          <h3 style={{
            fontSize: '2rem',
            color: '#00FFFF',
            marginBottom: '2rem',
            textAlign: 'center'
          }}>
            Recommended Jobs
          </h3>
          <div className="grid grid-cols-3">
            {jobs.recommended.map((job, index) => (
              <JobCard key={job.title} job={job} index={index} />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default JobsSection;
