import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  const languages = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' }
  ];

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">🔍</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">{t('header.opportunities')}</Link>
            <Link to="/mentors" className="nav-item">{t('header.mentors')}</Link>
            <Link to="/games" className="nav-item">{t('header.games')}</Link>
            <Link to="/badges" className="nav-item">{t('header.badges')}</Link>
            <Link to="/aipro" className="nav-item ai-pro">{t('header.aipro')}</Link>
            
            <div className="language-selector">
              <button onClick={toggleLanguage} className="language-btn">
                🌐 {languages.find(lang => lang.code === currentLanguage)?.name}
              </button>
              {isLanguageOpen && (
                <div className="language-dropdown">
                  {languages.map((language) => (
                    <button
                      key={language.code}
                      onClick={() => handleLanguageChange(language.code)}
                      className={`language-option ${currentLanguage === language.code ? 'active' : ''}`}
                    >
                      <span className="flag">{language.flag}</span>
                      <span className="name">{language.name}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
            
            <button onClick={toggleProfile} className="profile-btn">👤 {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
