import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [currentText, setCurrentText] = useState(0);
  
  const texts = [
    "Explore Opportunities",
    "Learn with Mentors", 
    "Play. Earn. Grow."
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % texts.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [texts.length]);

  return (
    <section style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated Background */}
      <div style={{
        position: 'absolute',
        inset: 0
      }}>
        <div className="float" style={{
          position: 'absolute',
          top: '20%',
          left: '10%',
          width: '300px',
          height: '300px',
          background: 'rgba(0, 255, 255, 0.2)',
          borderRadius: '50%',
          filter: 'blur(40px)'
        }}></div>
        <div className="float" style={{
          position: 'absolute',
          bottom: '20%',
          right: '10%',
          width: '400px',
          height: '400px',
          background: 'rgba(0, 191, 255, 0.2)',
          borderRadius: '50%',
          filter: 'blur(40px)',
          animationDelay: '2s'
        }}></div>
        <div className="float" style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '250px',
          height: '250px',
          background: 'rgba(128, 0, 255, 0.2)',
          borderRadius: '50%',
          filter: 'blur(40px)',
          animationDelay: '4s'
        }}></div>
      </div>

      {/* Content */}
      <div style={{
        textAlign: 'center',
        position: 'relative',
        zIndex: 10,
        padding: '0 1rem'
      }}>
        <h1 className="gradient-text fade-in-up" style={{
          fontSize: '6rem',
          fontWeight: '700',
          marginBottom: '1.5rem'
        }}>
          BriDGe
        </h1>
        
        <p className="fade-in-up" style={{
          fontSize: '2rem',
          color: '#00FFFF',
          marginBottom: '2rem',
          animationDelay: '0.3s'
        }}>
          Bridge the gap, Build the future
        </p>

        <div className="fade-in-up" style={{
          height: '2rem',
          marginBottom: '3rem',
          animationDelay: '0.6s'
        }}>
          <div key={currentText} style={{
            fontSize: '1.5rem',
            color: '#00BFFF',
            animation: 'fadeInUp 0.5s ease-out'
          }}>
            {texts[currentText]}
          </div>
        </div>

        <div className="fade-in-up" style={{
          display: 'flex',
          gap: '1rem',
          justifyContent: 'center',
          flexWrap: 'wrap',
          animationDelay: '0.9s'
        }}>
          <button className="btn-primary">
            Get Started
          </button>
          <button className="btn-secondary">
            Learn More
          </button>
        </div>
      </div>

      {/* Scroll Indicator */}
      <div style={{
        position: 'absolute',
        bottom: '2rem',
        left: '50%',
        transform: 'translateX(-50%)'
      }}>
        <div style={{
          width: '24px',
          height: '40px',
          border: '2px solid #00FFFF',
          borderRadius: '12px',
          display: 'flex',
          justifyContent: 'center'
        }}>
          <div style={{
            width: '4px',
            height: '12px',
            background: '#00FFFF',
            borderRadius: '2px',
            marginTop: '8px',
            animation: 'pulse 2s infinite'
          }}></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
