import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');

  return (
    <header className="header">
      <div className="container">
        <Link to="/" className="logo">BriDGe</Link>
        
        <div className="search-container">
          <input
            type="text"
            placeholder="Search opportunities, courses, mentors..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-bar"
          />
          <button className="search-btn">üîç</button>
        </div>

        <nav className="nav-menu">
          <Link to="/opportunities" className="nav-item">Opportunities</Link>
          <Link to="/mentors" className="nav-item">Mentors</Link>
          <Link to="/games" className="nav-item">Games</Link>
          <Link to="/badges" className="nav-item">Badges</Link>
          <Link to="/ai-pro" className="nav-item ai-pro">AI Pro</Link>
          <Link to="/profile" className="profile-btn">üë§ Profile</Link>
        </nav>
      </div>
    </header>
  );
}

export default Header;
