import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">BriDGe</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder="Search opportunities, courses, mentors..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">üîç</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">Opportunities</Link>
            <Link to="/mentors" className="nav-item">Mentors</Link>
            <Link to="/games" className="nav-item">Games</Link>
            <Link to="/badges" className="nav-item">Badges</Link>
            <Link to="/aipro" className="nav-item ai-pro">AI Pro</Link>
            <button onClick={toggleProfile} className="profile-btn">üë§ Profile</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
