import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();

  const navItems = [
    { name: 'Opportunities', path: '/' },
    { name: 'Mentors', path: '/' },
    { name: 'Games & Badges', path: '/games' },
    { name: 'AI Pro', path: '/' }
  ];

  return (
    <header className="glass" style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      zIndex: 1000,
      padding: '1rem 0'
    }}>
      <div className="container">
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          {/* Logo */}
          <Link to="/" style={{
            fontSize: '2rem',
            fontWeight: '700',
            textDecoration: 'none'
          }} className="gradient-text">
            BriDGe
          </Link>

          {/* Search Bar */}
          <div style={{
            flex: '1',
            maxWidth: '500px',
            margin: '0 2rem'
          }}>
            <div style={{ position: 'relative' }}>
              <div style={{
                position: 'absolute',
                left: '12px',
                top: '50%',
                transform: 'translateY(-50%)',
                color: '#00FFFF'
              }}>üîç</div>
              <input
                type="text"
                placeholder="Search opportunities, courses, mentors..."
                className="input-glass"
                style={{
                  width: '100%',
                  paddingLeft: '40px'
                }}
              />
            </div>
          </div>

          {/* Navigation */}
          <nav style={{
            display: 'flex',
            alignItems: 'center',
            gap: '1rem'
          }}>
            {navItems.map((item) => (
              <Link
                key={item.name}
                to={item.path}
                className="nav-link"
                style={{
                  backgroundColor: location.pathname === item.path ? 'rgba(0, 255, 255, 0.2)' : 'transparent'
                }}
              >
                {item.name}
              </Link>
            ))}
            
            {/* Profile Button */}
            <Link to="/profile">
              <button className="btn-secondary" style={{
                width: '40px',
                height: '40px',
                padding: 0,
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                üë§
              </button>
            </Link>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
