import React from 'react';

const NetworkingSection = () => {
  const networkingOptions = [
    {
      title: 'Connect with Mentors',
      description: 'Get guidance from industry experts and experienced professionals',
      people: [
        { name: 'Sarah Chen', role: 'Senior Product Manager', company: 'Google' },
        { name: 'Mike Rodriguez', role: 'Lead Developer', company: 'Microsoft' },
        { name: 'Emily Watson', role: 'UX Director', company: 'Apple' }
      ]
    },
    {
      title: 'Connect with Alumni',
      description: 'Network with graduates from your institution and build connections',
      people: [
        { name: 'Alex Thompson', role: 'Software Engineer', company: 'Amazon' },
        { name: 'Jessica Lee', role: 'Data Scientist', company: 'Netflix' },
        { name: 'David Kim', role: 'Product Designer', company: 'Meta' }
      ]
    },
    {
      title: 'Connect with Institutions',
      description: 'Partner with leading educational institutions and organizations',
      people: [
        { name: 'Stanford University', role: 'Higher Education', company: 'Education' },
        { name: 'MIT', role: 'Research Institution', company: 'Education' },
        { name: 'Harvard University', role: 'Higher Education', company: 'Education' }
      ]
    }
  ];

  const PersonAvatar = ({ person, index }) => (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      padding: '0.75rem',
      background: 'rgba(255, 255, 255, 0.05)',
      borderRadius: '8px',
      marginBottom: '0.5rem'
    }}>
      <div style={{
        width: '40px',
        height: '40px',
        background: 'linear-gradient(45deg, #00FFFF, #00BFFF)',
        borderRadius: '50%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginRight: '0.75rem',
        fontWeight: '600',
        color: 'white',
        fontSize: '0.8rem'
      }}>
        {person.name.split(' ').map(n => n[0]).join('')}
      </div>
      <div style={{ flex: 1 }}>
        <div style={{ color: 'white', fontSize: '0.9rem', fontWeight: '600' }}>
          {person.name}
        </div>
        <div style={{ color: '#00BFFF', fontSize: '0.8rem' }}>
          {person.role} â€¢ {person.company}
        </div>
      </div>
    </div>
  );

  return (
    <section className="section">
      <div className="container">
        <h2 className="section-title fade-in-up">
          Networking
        </h2>
        
        <div className="grid grid-cols-3">
          {networkingOptions.map((option, index) => (
            <div 
              key={option.title}
              className="card holographic"
              style={{ animationDelay: `${index * 0.2}s` }}
            >
              <h3 style={{
                fontSize: '1.5rem',
                color: 'white',
                marginBottom: '1rem',
                textAlign: 'center'
              }}>
                {option.title}
              </h3>
              
              <p style={{
                color: '#00BFFF',
                textAlign: 'center',
                marginBottom: '1.5rem',
                lineHeight: '1.4'
              }}>
                {option.description}
              </p>
              
              <div style={{ marginBottom: '2rem' }}>
                {option.people.map((person, personIndex) => (
                  <PersonAvatar 
                    key={person.name} 
                    person={person} 
                    index={personIndex}
                  />
                ))}
              </div>
              
              <button className="btn-primary" style={{ width: '100%' }}>
                Connect Now
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default NetworkingSection;
