import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();
  const languageRef = useRef(null);

  // Debug logging
  console.log('Header - Current Language:', currentLanguage);
  console.log('Header - Translation function working:', t('header.logo'));
  console.log('Header - Languages array:', languages);
  console.log('Header - isLanguageOpen:', isLanguageOpen);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  const languages = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' }
  ];

  // Close language dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageRef.current && !languageRef.current.contains(event.target)) {
        setIsLanguageOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">🔍</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">
              <span className="nav-icon">💼</span>
              {t('header.opportunities')}
            </Link>
            <Link to="/mentors" className="nav-item">
              <span className="nav-icon">👨‍🏫</span>
              {t('header.mentors')}
            </Link>
            <Link to="/games" className="nav-item">
              <span className="nav-icon">🎮</span>
              {t('header.games')}
            </Link>
            <Link to="/badges" className="nav-item">
              <span className="nav-icon">🏆</span>
              {t('header.badges')}
            </Link>
            <Link to="/aipro" className="nav-item ai-pro">
              <span className="nav-icon">🤖</span>
              {t('header.aipro')}
            </Link>
            
            <div className="language-selector" ref={languageRef}>
              <button 
                onClick={toggleLanguage} 
                className="language-btn"
                style={{
                  background: 'rgba(255,255,255,0.1)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.2)',
                  padding: '0.5rem 1rem',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.9rem'
                }}
              >
                🌐 {languages.find(lang => lang.code === currentLanguage)?.name || 'English'}
              </button>
              {isLanguageOpen && (
                <div 
                  className="language-dropdown"
                  style={{
                    position: 'absolute',
                    top: '100%',
                    right: '0',
                    background: 'white',
                    borderRadius: '12px',
                    boxShadow: '0 15px 35px rgba(0,0,0,0.25)',
                    padding: '0.5rem 0',
                    minWidth: '160px',
                    zIndex: '1003',
                    marginTop: '0.5rem'
                  }}
                >
                  {languages.map((language) => (
                    <button
                      key={language.code}
                      onClick={() => handleLanguageChange(language.code)}
                      className={`language-option ${currentLanguage === language.code ? 'active' : ''}`}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.75rem',
                        padding: '0.75rem 1rem',
                        border: 'none',
                        background: currentLanguage === language.code ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : 'transparent',
                        color: currentLanguage === language.code ? 'white' : '#333',
                        cursor: 'pointer',
                        width: '100%',
                        textAlign: 'left',
                        fontSize: '0.9rem'
                      }}
                    >
                      <span className="flag">{language.flag}</span>
                      <span className="name">{language.name}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
            
            <button onClick={toggleProfile} className="profile-btn">👤 {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
