import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProfileSidebar from './ProfileSidebar';
import { useLanguage } from '../contexts/LanguageContext';

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isLanguageOpen, setIsLanguageOpen] = useState(false);
  const { currentLanguage, changeLanguage, t } = useLanguage();
  const languageRef = useRef(null);

  const languages = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' }
  ];

  // Debug logging
  console.log('Header - Current Language:', currentLanguage);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const toggleLanguage = () => {
    console.log('Toggle language dropdown. Current state:', isLanguageOpen, 'New state:', !isLanguageOpen);
    setIsLanguageOpen(!isLanguageOpen);
  };

  const handleLanguageChange = (language) => {
    console.log('Changing language to:', language);
    changeLanguage(language);
    setIsLanguageOpen(false);
  };

  // Close language dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageRef.current && !languageRef.current.contains(event.target)) {
        setIsLanguageOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <Link to="/" className="logo">{t('header.logo')}</Link>
          
          <div className="search-container">
            <input
              type="text"
              placeholder={t('header.search')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-bar"
            />
            <button className="search-btn">🔍</button>
          </div>

          <nav className="nav-menu">
            <Link to="/opportunities" className="nav-item">
              <span className="nav-icon">💼</span>
              {t('header.opportunities')}
            </Link>
            <Link to="/mentors" className="nav-item">
              <span className="nav-icon">👨‍🏫</span>
              {t('header.mentors')}
            </Link>
            <Link to="/games" className="nav-item">
              <span className="nav-icon">🎮</span>
              {t('header.games')}
            </Link>
            <Link to="/badges" className="nav-item">
              <span className="nav-icon">🏆</span>
              {t('header.badges')}
            </Link>
            <Link to="/aipro" className="nav-item ai-pro">
              <span className="nav-icon">🤖</span>
              {t('header.aipro')}
            </Link>
            
            <div 
              className="language-selector" 
              ref={languageRef}
              style={{
                position: 'relative',
                display: 'inline-block',
                zIndex: '1002'
              }}
            >
              <button 
                onClick={toggleLanguage} 
                className="language-btn"
                style={{
                  background: isLanguageOpen ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.2)',
                  padding: '8px 16px',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  fontSize: '14px',
                  fontWeight: '500',
                  outline: 'none',
                  transition: 'all 0.3s ease',
                  whiteSpace: 'nowrap'
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = 'rgba(255,255,255,0.25)';
                  e.target.style.transform = 'translateY(-1px)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = isLanguageOpen ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)';
                  e.target.style.transform = 'translateY(0)';
                }}
              >
                🌐 {languages.find(lang => lang.code === currentLanguage)?.name || 'English'}
                <span style={{ fontSize: '12px', marginLeft: '4px' }}>
                  {isLanguageOpen ? '▲' : '▼'}
                </span>
              </button>
              {isLanguageOpen && (
                <div 
                  className="language-dropdown"
                  style={{
                    position: 'absolute',
                    top: '100%',
                    right: '0',
                    background: '#ffffff',
                    borderRadius: '12px',
                    boxShadow: '0 15px 35px rgba(0,0,0,0.25)',
                    padding: '8px 0',
                    minWidth: '180px',
                    zIndex: '9999',
                    marginTop: '8px',
                    border: '2px solid rgba(102, 126, 234, 0.2)',
                    overflow: 'visible'
                  }}
                >
                  {languages.map((language) => (
                    <button
                      key={language.code}
                      onClick={() => {
                        console.log('Language option clicked:', language.code, language.name);
                        handleLanguageChange(language.code);
                      }}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '12px',
                        padding: '12px 16px',
                        border: 'none',
                        background: currentLanguage === language.code ? '#667eea' : '#ffffff',
                        color: currentLanguage === language.code ? '#ffffff' : '#2d3748',
                        cursor: 'pointer',
                        width: '100%',
                        textAlign: 'left',
                        fontSize: '14px',
                        fontWeight: currentLanguage === language.code ? '600' : '500',
                        transition: 'all 0.2s ease',
                        outline: 'none'
                      }}
                      onMouseEnter={(e) => {
                        if (currentLanguage !== language.code) {
                          e.target.style.background = '#f7fafc';
                          e.target.style.color = '#667eea';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (currentLanguage !== language.code) {
                          e.target.style.background = '#ffffff';
                          e.target.style.color = '#2d3748';
                        }
                      }}
                    >
                      <span style={{ fontSize: '18px' }}>{language.flag}</span>
                      <span style={{ fontWeight: 'inherit', color: 'inherit' }}>{language.name}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
            

            <button onClick={toggleProfile} className="profile-btn">👤 {t('header.profile')}</button>
          </nav>
        </div>
      </header>
      
      <ProfileSidebar isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />
    </>
  );
}

export default Header;
