import React, { useState } from 'react';

function Badges() {
  const [activeTab, setActiveTab] = useState('all');
  const [selectedBadge, setSelectedBadge] = useState(null);

  const userStats = {
    totalBadges: 15,
    rareBadges: 8,
    epicBadges: 3,
    legendaryBadges: 2,
    bridgeProBadges: 5
  };

  const freemiumBadges = [
    {
      id: 1,
      name: 'Daily Warrior',
      description: 'Login for 7 consecutive days',
      icon: 'üó°Ô∏è',
      rarity: 'common',
      earned: true,
      earnedDate: '2024-10-15',
      progress: 7,
      maxProgress: 7,
      xp: 100,
      background: 'linear-gradient(135deg, #4ade80 0%, #22c55e 100%)'
    },
    {
      id: 2,
      name: 'Streak Master',
      description: 'Maintain a 30-day login streak',
      icon: 'üî•',
      rarity: 'rare',
      earned: true,
      earnedDate: '2024-10-10',
      progress: 30,
      maxProgress: 30,
      xp: 500,
      background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)'
    },
    {
      id: 3,
      name: 'Task Crusher',
      description: 'Complete 50 daily tasks',
      icon: '‚ö°',
      rarity: 'common',
      earned: true,
      earnedDate: '2024-10-12',
      progress: 50,
      maxProgress: 50,
      xp: 250,
      background: 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)'
    },
    {
      id: 4,
      name: 'Game Champion',
      description: 'Win 25 games across all platforms',
      icon: 'üèÜ',
      rarity: 'epic',
      earned: true,
      earnedDate: '2024-10-08',
      progress: 25,
      maxProgress: 25,
      xp: 750,
      background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)'
    },
    {
      id: 5,
      name: 'Hackathon Hero',
      description: 'Participate in 5 hackathons',
      icon: 'üíª',
      rarity: 'rare',
      earned: true,
      earnedDate: '2024-10-05',
      progress: 5,
      maxProgress: 5,
      xp: 600,
      background: 'linear-gradient(135deg, #06b6d4 0%, #0891b2 100%)'
    },
    {
      id: 6,
      name: 'Code Ninja',
      description: 'Solve 100 coding problems',
      icon: 'ü•∑',
      rarity: 'epic',
      earned: false,
      progress: 73,
      maxProgress: 100,
      xp: 1000,
      background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)'
    },
    {
      id: 7,
      name: 'Learning Enthusiast',
      description: 'Complete 10 courses',
      icon: 'üìö',
      rarity: 'common',
      earned: true,
      earnedDate: '2024-10-14',
      progress: 10,
      maxProgress: 10,
      xp: 300,
      background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)'
    },
    {
      id: 8,
      name: 'Social Butterfly',
      description: 'Connect with 20 mentors',
      icon: 'ü¶ã',
      rarity: 'rare',
      earned: false,
      progress: 12,
      maxProgress: 20,
      xp: 400,
      background: 'linear-gradient(135deg, #ec4899 0%, #db2777 100%)'
    }
  ];

  const proBadges = [
    {
      id: 101,
      name: 'BriDGe Elite',
      description: 'Exclusive Pro member badge',
      icon: 'üëë',
      rarity: 'legendary',
      earned: true,
      earnedDate: '2024-10-01',
      progress: 1,
      maxProgress: 1,
      xp: 2000,
      background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 50%, #d97706 100%)',
      isPro: true,
      perks: ['Priority Support', 'Exclusive Events', 'Advanced Analytics']
    },
    {
      id: 102,
      name: 'AI Whisperer',
      description: 'Master AI-powered features',
      icon: 'ü§ñ',
      rarity: 'epic',
      earned: true,
      earnedDate: '2024-10-03',
      progress: 1,
      maxProgress: 1,
      xp: 1500,
      background: 'linear-gradient(135deg, #6366f1 0%, #4f46e5 50%, #4338ca 100%)',
      isPro: true,
      perks: ['AI Career Guidance', 'Smart Recommendations', 'Personalized Learning']
    },
    {
      id: 103,
      name: 'Mentor Magnet',
      description: 'Connect with 50+ premium mentors',
      icon: 'üß≤',
      rarity: 'epic',
      earned: true,
      earnedDate: '2024-10-06',
      progress: 52,
      maxProgress: 50,
      xp: 1200,
      background: 'linear-gradient(135deg, #14b8a6 0%, #0d9488 50%, #0f766e 100%)',
      isPro: true,
      perks: ['1-on-1 Sessions', 'Group Mentoring', 'Industry Experts']
    },
    {
      id: 104,
      name: 'Innovation Pioneer',
      description: 'Lead 3 successful projects',
      icon: 'üöÄ',
      rarity: 'legendary',
      earned: false,
      progress: 2,
      maxProgress: 3,
      xp: 2500,
      background: 'linear-gradient(135deg, #f97316 0%, #ea580c 50%, #c2410c 100%)',
      isPro: true,
      perks: ['Project Showcase', 'Investor Connect', 'Startup Credits']
    },
    {
      id: 105,
      name: 'Global Networker',
      description: 'Build international connections',
      icon: 'üåç',
      rarity: 'rare',
      earned: true,
      earnedDate: '2024-10-09',
      progress: 1,
      maxProgress: 1,
      xp: 800,
      background: 'linear-gradient(135deg, #0ea5e9 0%, #0284c7 50%, #0369a1 100%)',
      isPro: true,
      perks: ['Global Events', 'Cross-border Opportunities', 'Cultural Exchange']
    }
  ];

  const specialBadges = [
    {
      id: 201,
      name: 'Founding Member',
      description: 'One of the first 1000 BriDGe users',
      icon: '‚≠ê',
      rarity: 'legendary',
      earned: true,
      earnedDate: '2024-09-15',
      progress: 1,
      maxProgress: 1,
      xp: 5000,
      background: 'linear-gradient(135deg, #a855f7 0%, #9333ea 25%, #7c3aed 50%, #6d28d9 75%, #5b21b6 100%)',
      isSpecial: true,
      perks: ['Lifetime Recognition', 'Exclusive Community', 'Beta Access']
    },
    {
      id: 202,
      name: 'Community Leader',
      description: 'Help 100+ students succeed',
      icon: 'üéñÔ∏è',
      rarity: 'legendary',
      earned: false,
      progress: 67,
      maxProgress: 100,
      xp: 3000,
      background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 25%, #991b1b 50%, #7f1d1d 75%, #450a0a 100%)',
      isSpecial: true,
      perks: ['Leadership Recognition', 'Mentoring Opportunities', 'Community Events']
    }
  ];

  const allBadges = [...freemiumBadges, ...proBadges, ...specialBadges];

  const filteredBadges = () => {
    switch(activeTab) {
      case 'earned':
        return allBadges.filter(badge => badge.earned);
      case 'pro':
        return proBadges;
      case 'special':
        return specialBadges;
      default:
        return allBadges;
    }
  };

  const getRarityColor = (rarity) => {
    switch(rarity) {
      case 'common': return '#10b981';
      case 'rare': return '#3b82f6';
      case 'epic': return '#8b5cf6';
      case 'legendary': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  const getRarityGlow = (rarity) => {
    switch(rarity) {
      case 'common': return '0 0 20px rgba(16, 185, 129, 0.5)';
      case 'rare': return '0 0 25px rgba(59, 130, 246, 0.6)';
      case 'epic': return '0 0 30px rgba(139, 92, 246, 0.7)';
      case 'legendary': return '0 0 35px rgba(245, 158, 11, 0.8)';
      default: return 'none';
    }
  };

  // Inline styles to ensure rendering
  const pageStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 25%, #16213e 50%, #0f3460 75%, #533483 100%)',
    position: 'relative',
    overflowX: 'hidden',
    color: 'white'
  };

  const heroStyle = {
    padding: '4rem 0 3rem 0',
    position: 'relative',
    zIndex: 2,
    textAlign: 'center'
  };

  const titleStyle = {
    fontSize: '4rem',
    fontWeight: 900,
    marginBottom: '1rem',
    textShadow: '0 0 20px rgba(102, 126, 234, 0.5)'
  };

  const gradientTextStyle = {
    background: 'linear-gradient(45deg, #fbbf24, #f59e0b, #d97706, #92400e)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    backgroundClip: 'text'
  };

  const subtitleStyle = {
    fontSize: '1.3rem',
    opacity: 0.8,
    marginBottom: '3rem'
  };

  const statsStyle = {
    display: 'flex',
    justifyContent: 'center',
    gap: '2rem',
    flexWrap: 'wrap',
    marginBottom: '3rem'
  };

  const statItemStyle = {
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(20px)',
    border: '2px solid rgba(255, 255, 255, 0.2)',
    borderRadius: '20px',
    padding: '1.5rem',
    textAlign: 'center',
    minWidth: '120px',
    transition: 'all 0.3s ease'
  };

  const filtersStyle = {
    display: 'flex',
    justifyContent: 'center',
    gap: '1rem',
    flexWrap: 'wrap'
  };

  const filterBtnStyle = {
    padding: '1rem 2rem',
    background: 'rgba(255, 255, 255, 0.1)',
    border: '2px solid rgba(255, 255, 255, 0.2)',
    color: 'white',
    borderRadius: '25px',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontWeight: 600,
    backdropFilter: 'blur(10px)'
  };

  const activeFilterStyle = {
    ...filterBtnStyle,
    background: 'linear-gradient(45deg, #fbbf24, #f59e0b)',
    borderColor: 'transparent',
    transform: 'translateY(-3px)',
    boxShadow: '0 15px 35px rgba(251, 191, 36, 0.4)'
  };

  const contentStyle = {
    padding: '3rem 0',
    position: 'relative',
    zIndex: 2
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '2rem',
    marginBottom: '4rem'
  };

  const cardStyle = {
    background: 'rgba(255, 255, 255, 0.05)',
    backdropFilter: 'blur(20px)',
    border: '2px solid rgba(255, 255, 255, 0.1)',
    borderRadius: '20px',
    padding: '2rem',
    cursor: 'pointer',
    transition: 'all 0.4s ease',
    position: 'relative',
    overflow: 'hidden'
  };

  const earnedCardStyle = {
    ...cardStyle,
    borderColor: 'rgba(251, 191, 36, 0.5)'
  };

  const lockedCardStyle = {
    ...cardStyle,
    opacity: 0.6
  };

  return (
    <div style={pageStyle}>
      {/* Hero Section */}
      <div style={heroStyle}>
        <div className="container">
          <h1 style={titleStyle}>
            Badge <span style={gradientTextStyle}>Collection</span>
          </h1>
          <p style={subtitleStyle}>
            Showcase your achievements and unlock exclusive rewards
          </p>

          {/* User Stats */}
          <div style={statsStyle}>
            <div style={statItemStyle}>
              <div style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>üèÖ</div>
              <div style={{ fontSize: '2rem', fontWeight: 800, color: '#fbbf24', marginBottom: '0.3rem' }}>
                {userStats.totalBadges}
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>Total Badges</div>
            </div>
            <div style={statItemStyle}>
              <div style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>üíé</div>
              <div style={{ fontSize: '2rem', fontWeight: 800, color: '#fbbf24', marginBottom: '0.3rem' }}>
                {userStats.rareBadges}
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>Rare Badges</div>
            </div>
            <div style={statItemStyle}>
              <div style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>‚ö°</div>
              <div style={{ fontSize: '2rem', fontWeight: 800, color: '#fbbf24', marginBottom: '0.3rem' }}>
                {userStats.epicBadges}
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>Epic Badges</div>
            </div>
            <div style={statItemStyle}>
              <div style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>üëë</div>
              <div style={{ fontSize: '2rem', fontWeight: 800, color: '#fbbf24', marginBottom: '0.3rem' }}>
                {userStats.legendaryBadges}
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>Legendary</div>
            </div>
          </div>

          {/* Filter Tabs */}
          <div style={filtersStyle}>
            <button 
              style={activeTab === 'all' ? activeFilterStyle : filterBtnStyle}
              onClick={() => setActiveTab('all')}
            >
              All Badges
            </button>
            <button 
              style={activeTab === 'earned' ? activeFilterStyle : filterBtnStyle}
              onClick={() => setActiveTab('earned')}
            >
              Earned
            </button>
            <button 
              style={activeTab === 'pro' ? activeFilterStyle : filterBtnStyle}
              onClick={() => setActiveTab('pro')}
            >
              BriDGe Pro
            </button>
            <button 
              style={activeTab === 'special' ? activeFilterStyle : filterBtnStyle}
              onClick={() => setActiveTab('special')}
            >
              Special
            </button>
          </div>
        </div>
      </div>

      {/* Badges Grid */}
      <div style={contentStyle}>
        <div className="container">
          <div style={gridStyle}>
            {filteredBadges().map((badge, index) => (
              <div 
                key={badge.id} 
                style={badge.earned ? earnedCardStyle : lockedCardStyle}
                onClick={() => setSelectedBadge(badge)}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'translateY(-10px)';
                  e.target.style.boxShadow = badge.earned ? getRarityGlow(badge.rarity) : '0 20px 40px rgba(0,0,0,0.3)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = 'none';
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
                  {badge.isPro && (
                    <div style={{
                      background: 'linear-gradient(45deg, #fbbf24, #f59e0b)',
                      color: 'white',
                      padding: '0.3rem 0.8rem',
                      borderRadius: '15px',
                      fontSize: '0.7rem',
                      fontWeight: 700,
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      PRO
                    </div>
                  )}
                  {badge.isSpecial && (
                    <div style={{
                      background: 'linear-gradient(45deg, #a855f7, #9333ea)',
                      color: 'white',
                      padding: '0.3rem 0.8rem',
                      borderRadius: '15px',
                      fontSize: '0.7rem',
                      fontWeight: 700,
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      SPECIAL
                    </div>
                  )}
                  <div style={{ 
                    fontSize: '0.8rem',
                    fontWeight: 700,
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px',
                    color: getRarityColor(badge.rarity)
                  }}>
                    {badge.rarity.toUpperCase()}
                  </div>
                </div>

                <div style={{
                  width: '120px',
                  height: '120px',
                  borderRadius: '20px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: '0 auto 1.5rem auto',
                  position: 'relative',
                  boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)',
                  background: badge.background
                }}>
                  <div style={{ fontSize: '4rem', zIndex: 2 }}>{badge.icon}</div>
                  {!badge.earned && (
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'rgba(0, 0, 0, 0.7)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '2rem',
                      borderRadius: '20px'
                    }}>
                      üîí
                    </div>
                  )}
                </div>

                <div style={{ textAlign: 'center' }}>
                  <h3 style={{
                    fontSize: '1.3rem',
                    fontWeight: 700,
                    marginBottom: '0.5rem',
                    background: 'linear-gradient(45deg, #fff, #e0e7ff)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text'
                  }}>
                    {badge.name}
                  </h3>
                  <p style={{
                    opacity: 0.8,
                    lineHeight: 1.4,
                    marginBottom: '1rem',
                    fontSize: '0.9rem'
                  }}>
                    {badge.description}
                  </p>
                  
                  {!badge.earned && (
                    <div>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.1)',
                        borderRadius: '10px',
                        height: '8px',
                        position: 'relative',
                        marginBottom: '0.5rem',
                        overflow: 'hidden'
                      }}>
                        <div style={{
                          height: '100%',
                          borderRadius: '10px',
                          width: `${(badge.progress / badge.maxProgress) * 100}%`,
                          background: getRarityColor(badge.rarity),
                          transition: 'width 0.3s ease'
                        }}></div>
                      </div>
                      <span style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                        {badge.progress}/{badge.maxProgress}
                      </span>
                    </div>
                  )}

                  {badge.earned && (
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      fontSize: '0.8rem'
                    }}>
                      <span style={{ opacity: 0.7 }}>
                        Earned: {new Date(badge.earnedDate).toLocaleDateString()}
                      </span>
                      <span style={{
                        background: 'linear-gradient(45deg, #48bb78, #38a169)',
                        color: 'white',
                        padding: '0.3rem 0.8rem',
                        borderRadius: '15px',
                        fontWeight: 600
                      }}>
                        +{badge.xp} XP
                      </span>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>

          {/* Pro Upgrade Section */}
          <div style={{ marginTop: '4rem' }}>
            <div style={{
              background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 50%, #d97706 100%)',
              borderRadius: '25px',
              padding: '3rem',
              textAlign: 'center',
              color: 'white',
              position: 'relative',
              overflow: 'hidden'
            }}>
              <div style={{ position: 'relative', zIndex: 2, marginBottom: '2rem' }}>
                <h2 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>üöÄ Unlock BriDGe Pro Badges</h2>
                <p>Get exclusive badges and premium perks</p>
              </div>
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '1rem',
                marginBottom: '2rem',
                position: 'relative',
                zIndex: 2
              }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  background: 'rgba(255, 255, 255, 0.1)',
                  padding: '1rem',
                  borderRadius: '15px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '1.5rem' }}>üëë</span>
                  <span>Exclusive Pro Badges</span>
                </div>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  background: 'rgba(255, 255, 255, 0.1)',
                  padding: '1rem',
                  borderRadius: '15px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '1.5rem' }}>‚ö°</span>
                  <span>Faster Badge Progression</span>
                </div>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  background: 'rgba(255, 255, 255, 0.1)',
                  padding: '1rem',
                  borderRadius: '15px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '1.5rem' }}>üéØ</span>
                  <span>Priority Support</span>
                </div>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  background: 'rgba(255, 255, 255, 0.1)',
                  padding: '1rem',
                  borderRadius: '15px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '1.5rem' }}>üåü</span>
                  <span>Special Recognition</span>
                </div>
              </div>
              <button style={{
                background: 'rgba(255, 255, 255, 0.2)',
                color: 'white',
                border: '2px solid rgba(255, 255, 255, 0.3)',
                padding: '1rem 3rem',
                borderRadius: '25px',
                fontSize: '1.1rem',
                fontWeight: 700,
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                position: 'relative',
                zIndex: 2
              }}>
                Upgrade to Pro
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Badges;
