import React from 'react';

const FeaturedUpdates = () => {
  const updates = [
    {
      title: 'Samsung Galaxy AI Treasure Hunt',
      company: 'Samsung',
      participants: '1.2K Applied',
      daysLeft: '5 days left'
    },
    {
      title: 'Founder Of The Year 5.0',
      company: 'Annual Flagship Case Study',
      participants: '49 Applied',
      daysLeft: '21 days left'
    },
    {
      title: 'Ideathon Asia',
      company: 'National Institute of Technology',
      participants: '53 Applied',
      daysLeft: '11 days left'
    }
  ];

  return (
    <section className="section">
      <div className="container">
        <h2 className="section-title fade-in-up">
          Featured Updates
        </h2>
        
        <div className="grid grid-cols-3">
          {updates.map((update, index) => (
            <div 
              key={update.title}
              className="card fade-in-up holographic"
              style={{ animationDelay: `${index * 0.2}s` }}
            >
              <h3 style={{
                fontSize: '1.5rem',
                fontWeight: '600',
                marginBottom: '0.5rem',
                color: 'white'
              }}>
                {update.title}
              </h3>
              <p style={{
                color: '#00FFFF',
                marginBottom: '1rem'
              }}>
                {update.company}
              </p>
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                color: '#00BFFF',
                fontSize: '0.9rem'
              }}>
                <span>{update.participants}</span>
                <span>{update.daysLeft}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FeaturedUpdates;
