import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function ProfileEdit() {
  console.log('🎉 ProfileEdit component loaded successfully!');
  console.log('Current URL:', window.location.href);
  
  const [activeSection, setActiveSection] = useState('personal');
  const [profileImage, setProfileImage] = useState('/profile-avatar.svg');
  const [coverImage, setCoverImage] = useState('');

  const [formData, setFormData] = useState({
    // Personal Information
    firstName: 'Arjun',
    lastName: 'Sharma',
    email: 'arjun.sharma@student.edu',
    phone: '+91 9876543210',
    dateOfBirth: '1999-05-15',
    gender: 'Male',
    nationality: 'Indian',
    location: 'Bangalore, India',
    address: '123 Tech Street, Koramangala, Bangalore, Karnataka 560034',
    bio: 'Passionate computer science student with expertise in full-stack development and AI/ML.',
    linkedinUrl: 'https://linkedin.com/in/arjunsharma',
    githubUrl: 'https://github.com/arjunsharma',
    portfolioUrl: 'https://arjunsharma.dev',
    
    // Education Details
    currentEducation: {
      institution: 'Indian Institute of Technology',
      degree: 'Bachelor of Technology',
      field: 'Computer Science Engineering',
      startYear: '2021',
      endYear: '2025',
      cgpa: '8.7',
      semester: '7th Semester',
      percentage: '87%'
    },
    
    // Additional Education
    previousEducation: [
      {
        institution: 'Delhi Public School',
        degree: '12th Grade',
        field: 'Science (PCM)',
        year: '2021',
        percentage: '94.5%',
        board: 'CBSE'
      },
      {
        institution: 'Delhi Public School',
        degree: '10th Grade',
        field: 'All Subjects',
        year: '2019',
        percentage: '96.2%',
        board: 'CBSE'
      }
    ],
    
    // Skills
    technicalSkills: ['React', 'Node.js', 'Python', 'JavaScript', 'MongoDB', 'AWS', 'Docker', 'Git'],
    softSkills: ['Leadership', 'Communication', 'Problem Solving', 'Team Work'],
    languages: ['English', 'Hindi', 'Kannada'],
    
    // Experience & Projects
    workExperience: [
      {
        company: 'TechCorp Solutions',
        position: 'Software Development Intern',
        startDate: '2024-06',
        endDate: '2024-08',
        description: 'Developed web applications using React and Node.js',
        location: 'Bangalore, India'
      }
    ],
    
    projects: [
      {
        name: 'E-commerce Platform',
        description: 'Full-stack e-commerce application with payment integration',
        technologies: ['React', 'Node.js', 'MongoDB'],
        link: 'https://github.com/arjun/ecommerce',
        startDate: '2024-01',
        endDate: '2024-03'
      }
    ]
  });

  const sections = [
    { id: 'personal', name: 'Personal Info', icon: '👤' },
    { id: 'education', name: 'Education', icon: '🎓' },
    { id: 'skills', name: 'Skills', icon: '💡' },
    { id: 'experience', name: 'Experience & Projects', icon: '💼' },
    { id: 'additional', name: 'Additional Info', icon: '📋' }
  ];

  const handleInputChange = (section, field, value, index = null) => {
    setFormData(prev => {
      if (index !== null) {
        const newArray = [...prev[section]];
        newArray[index] = { ...newArray[index], [field]: value };
        return { ...prev, [section]: newArray };
      } else if (section === 'currentEducation') {
        return {
          ...prev,
          [section]: { ...prev[section], [field]: value }
        };
      } else {
        return { ...prev, [field]: value };
      }
    });
  };

  const handleImageUpload = (type, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (type === 'profile') {
          setProfileImage(e.target.result);
        } else {
          setCoverImage(e.target.result);
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const addArrayItem = (section, newItem) => {
    setFormData(prev => ({
      ...prev,
      [section]: [...prev[section], newItem]
    }));
  };

  const removeArrayItem = (section, index) => {
    setFormData(prev => ({
      ...prev,
      [section]: prev[section].filter((_, i) => i !== index)
    }));
  };

  const renderPersonalInfo = () => (
    <div style={{ marginBottom: '2rem' }}>
      <h3 style={{ color: '#feca57', marginBottom: '1.5rem', fontSize: '1.5rem' }}>
        👤 Personal Information
      </h3>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem' }}>
        <div>
          <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
            First Name
          </label>
          <input
            type="text"
            value={formData.firstName}
            onChange={(e) => handleInputChange(null, 'firstName', e.target.value)}
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
          />
        </div>
        <div>
          <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
            Last Name
          </label>
          <input
            type="text"
            value={formData.lastName}
            onChange={(e) => handleInputChange(null, 'lastName', e.target.value)}
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
          />
        </div>
        <div>
          <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
            Email
          </label>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => handleInputChange(null, 'email', e.target.value)}
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
          />
        </div>
        <div>
          <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
            Phone
          </label>
          <input
            type="tel"
            value={formData.phone}
            onChange={(e) => handleInputChange(null, 'phone', e.target.value)}
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
          />
        </div>
      </div>
    </div>
  );

  const renderExperienceAndProjects = () => (
    <div style={{ marginBottom: '2rem' }}>
      <h3 style={{ color: '#feca57', marginBottom: '1.5rem', fontSize: '1.5rem' }}>
        💼 Experience & Projects
      </h3>
      
      {/* Work Experience */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '15px',
        padding: '2rem',
        marginBottom: '2rem'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
          <h4 style={{ color: 'white', margin: 0 }}>Work Experience</h4>
          <button
            onClick={() => addArrayItem('workExperience', {
              company: '',
              position: '',
              startDate: '',
              endDate: '',
              description: ''
            })}
            style={{
              background: 'linear-gradient(45deg, #00ff88, #00d4ff)',
              border: 'none',
              color: 'white',
              padding: '0.8rem 1.5rem',
              borderRadius: '25px',
              fontWeight: '600',
              cursor: 'pointer'
            }}
          >
            + Add Experience
          </button>
        </div>
        
        {formData.workExperience.map((exp, index) => (
          <div key={index} style={{
            background: 'rgba(255, 255, 255, 0.05)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            borderRadius: '10px',
            padding: '1.5rem',
            marginBottom: '1rem'
          }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Company
                </label>
                <input
                  type="text"
                  value={exp.company}
                  onChange={(e) => handleInputChange('workExperience', 'company', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Position
                </label>
                <input
                  type="text"
                  value={exp.position}
                  onChange={(e) => handleInputChange('workExperience', 'position', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Start Date
                </label>
                <input
                  type="month"
                  value={exp.startDate}
                  onChange={(e) => handleInputChange('workExperience', 'startDate', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  End Date
                </label>
                <input
                  type="month"
                  value={exp.endDate}
                  onChange={(e) => handleInputChange('workExperience', 'endDate', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div style={{ gridColumn: '1 / -1' }}>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Description
                </label>
                <textarea
                  value={exp.description}
                  onChange={(e) => handleInputChange('workExperience', 'description', e.target.value, index)}
                  rows="3"
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem',
                    resize: 'vertical'
                  }}
                />
              </div>
            </div>
            <button
              onClick={() => removeArrayItem('workExperience', index)}
              style={{
                background: 'linear-gradient(45deg, #ff6b6b, #ff8e8e)',
                border: 'none',
                color: 'white',
                padding: '0.6rem 1.2rem',
                borderRadius: '20px',
                fontWeight: '600',
                cursor: 'pointer',
                marginTop: '1rem'
              }}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
      
      {/* Projects */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '15px',
        padding: '2rem'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
          <h4 style={{ color: 'white', margin: 0 }}>Projects</h4>
          <button
            onClick={() => addArrayItem('projects', {
              name: '',
              description: '',
              technologies: [],
              link: ''
            })}
            style={{
              background: 'linear-gradient(45deg, #00ff88, #00d4ff)',
              border: 'none',
              color: 'white',
              padding: '0.8rem 1.5rem',
              borderRadius: '25px',
              fontWeight: '600',
              cursor: 'pointer'
            }}
          >
            + Add Project
          </button>
        </div>
        
        {formData.projects.map((project, index) => (
          <div key={index} style={{
            background: 'rgba(255, 255, 255, 0.05)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            borderRadius: '10px',
            padding: '1.5rem',
            marginBottom: '1rem'
          }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Project Name
                </label>
                <input
                  type="text"
                  value={project.name}
                  onChange={(e) => handleInputChange('projects', 'name', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Project Link
                </label>
                <input
                  type="url"
                  value={project.link}
                  onChange={(e) => handleInputChange('projects', 'link', e.target.value, index)}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem'
                  }}
                />
              </div>
              <div style={{ gridColumn: '1 / -1' }}>
                <label style={{ display: 'block', color: '#00BFFF', marginBottom: '0.5rem', fontWeight: '600' }}>
                  Description
                </label>
                <textarea
                  value={project.description}
                  onChange={(e) => handleInputChange('projects', 'description', e.target.value, index)}
                  rows="3"
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: '2px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '8px',
                    color: 'white',
                    fontSize: '0.9rem',
                    resize: 'vertical'
                  }}
                />
              </div>
            </div>
            <button
              onClick={() => removeArrayItem('projects', index)}
              style={{
                background: 'linear-gradient(45deg, #ff6b6b, #ff8e8e)',
                border: 'none',
                color: 'white',
                padding: '0.6rem 1.2rem',
                borderRadius: '20px',
                fontWeight: '600',
                cursor: 'pointer',
                marginTop: '1rem'
              }}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  const renderAdditionalInfo = () => (
    <div style={{ marginBottom: '2rem' }}>
      <h3 style={{ color: '#feca57', marginBottom: '1.5rem', fontSize: '1.5rem' }}>
        📋 Additional Information
      </h3>
      
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem' }}>
        {/* Achievements */}
        <div>
          <h4 style={{ color: 'white', marginBottom: '1rem' }}>🏆 Achievements & Awards</h4>
          <textarea
            rows="4"
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem',
              resize: 'vertical'
            }}
            placeholder="List your achievements, awards, certifications, competitions won..."
          />
        </div>
        
        {/* Extracurricular */}
        <div>
          <h4 style={{ color: 'white', marginBottom: '1rem' }}>🎯 Extracurricular Activities</h4>
          <textarea
            rows="4"
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem',
              resize: 'vertical'
            }}
            placeholder="Clubs, sports, volunteer work, leadership roles..."
          />
        </div>
        
        {/* Career Goals */}
        <div style={{ gridColumn: '1 / -1' }}>
          <h4 style={{ color: 'white', marginBottom: '1rem' }}>🎯 Career Goals & Interests</h4>
          <textarea
            rows="4"
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem',
              resize: 'vertical'
            }}
            placeholder="Describe your career aspirations, areas of interest, future goals..."
          />
        </div>
        
        {/* Preferred Job Roles */}
        <div>
          <h4 style={{ color: 'white', marginBottom: '1rem' }}>💼 Preferred Job Roles</h4>
          <input
            type="text"
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
            placeholder="Software Developer, Data Scientist, Product Manager..."
          />
        </div>
        
        {/* Expected Salary */}
        <div>
          <h4 style={{ color: 'white', marginBottom: '1rem' }}>💰 Expected Salary Range</h4>
          <input
            type="text"
            style={{
              width: '100%',
              padding: '1rem',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '2px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: 'white',
              fontSize: '1rem'
            }}
            placeholder="₹5-8 LPA, ₹10-15 LPA, etc."
          />
        </div>
      </div>
    </div>
  );

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white'
    }}>
      {/* Header */}
      <div style={{
        height: '250px',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #ff6b6b 100%)',
        position: 'relative',
        display: 'flex',
        alignItems: 'end',
        padding: '2rem'
      }}>
        <div style={{
          position: 'absolute',
          top: '20px',
          right: '20px'
        }}>
          <Link
            to="/profile"
            style={{
              background: 'rgba(255, 255, 255, 0.2)',
              color: 'white',
              textDecoration: 'none',
              padding: '0.8rem 1.5rem',
              borderRadius: '25px',
              fontWeight: '600'
            }}
          >
            ← Back to Profile
          </Link>
        </div>
        
        <div style={{ display: 'flex', alignItems: 'end', gap: '2rem' }}>
          <div style={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            background: 'rgba(255, 255, 255, 0.2)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '3rem',
            border: '4px solid rgba(255, 255, 255, 0.3)'
          }}>
            👤
          </div>
          
          <div>
            <h1 style={{ fontSize: '2.5rem', marginBottom: '0.5rem' }}>
              {formData.firstName} {formData.lastName}
            </h1>
            <p style={{ fontSize: '1.2rem', opacity: 0.9 }}>
              {formData.email}
            </p>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        padding: '0 2rem',
        borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
        position: 'sticky',
        top: '0',
        zIndex: 100
      }}>
        <div style={{
          display: 'flex',
          gap: '0',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          {sections.map(section => (
            <button
              key={section.id}
              onClick={() => setActiveSection(section.id)}
              style={{
                background: activeSection === section.id ? 'rgba(254, 202, 87, 0.1)' : 'transparent',
                border: 'none',
                color: activeSection === section.id ? '#feca57' : 'rgba(255, 255, 255, 0.7)',
                padding: '1.5rem 2rem',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                gap: '0.8rem',
                fontWeight: '600',
                borderBottom: activeSection === section.id ? '3px solid #feca57' : '3px solid transparent'
              }}
            >
              <span style={{ fontSize: '1.2rem' }}>{section.icon}</span>
              <span>{section.name}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Content Area */}
      <div style={{
        padding: '3rem 2rem',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.08)',
          border: '1px solid rgba(255, 255, 255, 0.15)',
          borderRadius: '25px',
          padding: '2.5rem'
        }}>
          {activeSection === 'personal' && renderPersonalInfo()}
          {activeSection === 'education' && (
            <div style={{ textAlign: 'center', padding: '3rem' }}>
              <h3 style={{ color: '#feca57', marginBottom: '1rem' }}>🎓 Education Section</h3>
              <p style={{ opacity: 0.8 }}>Education details coming soon...</p>
            </div>
          )}
          {activeSection === 'skills' && (
            <div style={{ textAlign: 'center', padding: '3rem' }}>
              <h3 style={{ color: '#feca57', marginBottom: '1rem' }}>💡 Skills Section</h3>
              <p style={{ opacity: 0.8 }}>Skills management coming soon...</p>
            </div>
          )}
          {activeSection === 'experience' && renderExperienceAndProjects()}
          {activeSection === 'additional' && renderAdditionalInfo()}
          
          {/* Save Button */}
          <div style={{
            display: 'flex',
            gap: '1.5rem',
            justifyContent: 'center',
            marginTop: '3rem'
          }}>
            <button style={{
              background: 'linear-gradient(45deg, #feca57, #ff9ff3)',
              border: 'none',
              color: 'white',
              padding: '1.2rem 3rem',
              borderRadius: '30px',
              fontSize: '1.1rem',
              fontWeight: '700',
              cursor: 'pointer'
            }}>
              💾 Save Changes
            </button>
            
            <Link
              to="/profile"
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                border: '2px solid rgba(255, 255, 255, 0.3)',
                color: 'white',
                textDecoration: 'none',
                padding: '1.2rem 3rem',
                borderRadius: '30px',
                fontSize: '1.1rem',
                fontWeight: '700'
              }}
            >
              👁️ Preview Profile
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfileEdit;
