import React, { useState } from 'react';
import './Dashboard.css';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');

  const tabs = [
    { id: 'overview', label: 'Overview' },
    { id: 'opportunities', label: 'Opportunities' },
    { id: 'skills', label: 'Skills' },
    { id: 'progress', label: 'Progress' }
  ];

  const stats = [
    { value: '85%', label: 'Profile Score' },
    { value: '12', label: 'Applications' },
    { value: '5', label: 'Matches' },
    { value: '3', label: 'Interviews' }
  ];

  const skills = ['Python', 'JavaScript', 'React', 'SQL', 'Data Structures'];
  const careerPaths = ['Full Stack Developer', 'Data Scientist', 'DevOps Engineer'];

  const skillGaps = [
    { name: 'Cloud Computing (AWS/Azure)', priority: 'High', progress: 30 },
    { name: 'Machine Learning', priority: 'Medium', progress: 50 }
  ];

  return (
    <section id="dashboard" className="section">
      <div className="section-title">
        <h2>Your Career Dashboard</h2>
        <p>Personalized insights and recommendations to guide your career journey</p>
      </div>
      <div className="dashboard-preview glass">
        <div className="dashboard-tabs">
          {tabs.map(tab => (
            <button
              key={tab.id}
              className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>
        <div className="dashboard-content">
          <div className="profile-sidebar glass">
            <div className="profile-header">
              <div className="profile-avatar">
                <i className="fas fa-user"></i>
              </div>
              <div>
                <h3>Alex Johnson</h3>
                <p>Computer Science Student</p>
              </div>
            </div>
            <div className="profile-stats">
              {stats.map((stat, index) => (
                <div key={index} className="stat glass">
                  <div className="stat-value">{stat.value}</div>
                  <div className="stat-label">{stat.label}</div>
                </div>
              ))}
            </div>
            <h4>Top Skills</h4>
            <div className="opp-skills">
              {skills.map((skill, index) => (
                <span key={index} className="skill-tag">{skill}</span>
              ))}
            </div>
          </div>
          <div className="dashboard-main">
            <div className="recommendation-card glass">
              <div className="card-header">
                <h3>Career Recommendations</h3>
                <button className="btn btn-outline">View All</button>
              </div>
              <p>Based on your profile, skills, and interests, we recommend exploring these career paths:</p>
              <div className="opp-skills">
                {careerPaths.map((path, index) => (
                  <span key={index} className="skill-tag">{path}</span>
                ))}
              </div>
            </div>
            <div className="recommendation-card glass">
              <div className="card-header">
                <h3>Skill Gap Analysis</h3>
                <button className="btn btn-outline">Improve Plan</button>
              </div>
              <p>To become more competitive for your target roles, consider developing these skills:</p>
              {skillGaps.map((skill, index) => (
                <div key={index} className="skill-item">
                  <div className="skill-info">
                    <span>{skill.name}</span>
                    <span>Priority: {skill.priority}</span>
                  </div>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{ width: `${skill.progress}%` }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Dashboard;
