import React, { useEffect, useState } from 'react';
import { useScrollAnimation } from '../../hooks/useScrollAnimation';
import './Hero.css';

const Hero = () => {
  const [ref, isVisible] = useScrollAnimation(0.3);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth - 0.5) * 20,
        y: (e.clientY / window.innerHeight - 0.5) * 20
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return (
    <section id="home" className="hero section-gradient">
      {/* Animated Background Elements */}
      <div className="hero-background">
        <div className="floating-orb orb-1"></div>
        <div className="floating-orb orb-2"></div>
        <div className="floating-orb orb-3"></div>
      </div>

      <div 
        ref={ref} 
        className={`hero-content ${isVisible ? 'visible' : ''}`}
        style={{
          transform: `translateX(${mousePosition.x * 0.5}px) translateY(${mousePosition.y * 0.5}px)`
        }}
      >
        <div className="hero-text scroll-slide-left magnetic-hover">
          <h1 className="gradient-text text-glow">
            Transform Your Career Journey with <span className="text-glow-accent">AI-Powered</span> Insights
          </h1>
          <p className="text-glow">
            CareerConnect bridges the gap between talent and opportunity. Our platform uses 
            advanced AI to match students with ideal career paths, internships, and jobs while 
            helping employers find the perfect candidates.
          </p>
          <div className="hero-btns">
            <button className="btn btn-primary glass-neon shimmer">
              <span className="btn-content">
                <i className="fas fa-rocket"></i>
                Get Started
              </span>
            </button>
            <button className="btn btn-outline glass-liquid shimmer">
              <span className="btn-content">
                <i className="fas fa-play"></i>
                Watch Demo
              </span>
            </button>
          </div>
        </div>
        
        <div className="hero-image scroll-slide-right">
          <InteractiveCard 
            title="Profile Completion"
            progress={85}
            description="Complete your profile to get personalized recommendations"
            delay={0}
            type="neon"
          />
          <InteractiveCard 
            title="Skill Analysis"
            description="AI-powered assessment of your skills and growth areas"
            delay={2}
            type="holographic"
          />
          <InteractiveCard 
            title="Job Matches"
            description="5 new opportunities match your profile"
            delay={4}
            type="liquid"
          />
        </div>
      </div>
    </section>
  );
};

const InteractiveCard = ({ title, progress, description, delay, type }) => (
  <div 
    className={`interactive-card glass-${type} floating magnetic-hover`}
    style={{ 
      animationDelay: `${delay}s`,
      transitionDelay: `${delay * 0.2}s`
    }}
  >
    <div className="card-header">
      <h3 className="gradient-text">{title}</h3>
      {progress && (
        <div className="progress-indicator">
          <div className="progress-circle">
            <svg width="60" height="60" viewBox="0 0 60 60">
              <circle cx="30" cy="30" r="28" stroke="rgba(255,255,255,0.1)" strokeWidth="4" fill="none"/>
              <circle 
                cx="30" cy="30" r="28" 
                stroke="url(#gradient)" 
                strokeWidth="4" 
                fill="none"
                strokeLinecap="round"
                strokeDasharray={`${progress * 1.76} 176`}
                transform="rotate(-90 30 30)"
              />
            </svg>
            <span className="progress-text">{progress}%</span>
          </div>
        </div>
      )}
    </div>
    <p>{description}</p>
    <div className="card-glow"></div>
    
    {/* SVG for progress circle gradient */}
    <svg width="0" height="0">
      <defs>
        <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor="#4cc9f0" />
          <stop offset="100%" stopColor="#4361ee" />
        </linearGradient>
      </defs>
    </svg>
  </div>
);

export default Hero;
