import React, { useState } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import FreemiumFeatures from './components/FreemiumFeatures';
import OpportunitiesStatic from './components/Opportunities/OpportunitiesStatic';
import Dashboard from './components/Dashboard';
import Footer from './components/Footer';
import LoginPage from './components/LoginPage';
import ProfileCompletionForm from './components/ProfileCompletionForm';
import StudentForm from './components/StudentForm';
import BridgeAppProfile from './components/BridgeAppProfile';
import ScrollTriggeredModal from './components/ScrollTriggeredModal';

import './styles/globals.css';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'login', 'profile', 'studentForm', 'bridgeAppProfile'
  const [userData, setUserData] = useState(null);

  // Listen for messages from embedded components or iframes
  React.useEffect(() => {
    const handleMessage = (event) => {
      if (event.data.type === 'NAVIGATE_TO_PROFILE') {
        handleNavigateToProfile();
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  const handleLoginSuccess = (data) => {
    setUserData(data);
    setCurrentPage('profile');
  };

  const handleProfileComplete = (profileData) => {
    const updatedUserData = { ...userData, ...profileData };
    setUserData(updatedUserData);

    // Navigate to appropriate form based on user role
    if (userData.userType === 'student') {
      setCurrentPage('studentForm');
    } else if (userData.userType === 'employer') {
      setCurrentPage('home'); // For now, redirect employers back to home
    } else if (userData.userType === 'institution') {
      setCurrentPage('home'); // For now, redirect institutions back to home
    }
  };

  const handleBackToProfile = () => {
    setCurrentPage('profile');
  };

  const handleBackToLogin = () => {
    setCurrentPage('login');
    setUserData(null);
  };

  const handleBackToHome = () => {
    setCurrentPage('home');
  };

  const handleShowLogin = () => {
    setCurrentPage('login');
  };

  const handleNavigateToProfile = () => {
    setCurrentPage('bridgeAppProfile');
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'login':
        return <LoginPage onLoginSuccess={handleLoginSuccess} onClose={handleBackToHome} />;

      case 'profile':
        return (
          <ProfileCompletionForm
            userRole={userData?.userType}
            initialData={userData}
            onSubmit={handleProfileComplete}
            onBack={handleBackToLogin}
          />
        );

      case 'studentForm':
        return (
          <StudentForm
            studentData={userData}
            onBack={handleBackToProfile}
            onSkipToProfile={handleNavigateToProfile}
          />
        );

      case 'bridgeAppProfile':
        return (
          <BridgeAppProfile
            userData={userData}
            onBack={handleBackToHome}
          />
        );

      case 'home':
      default:
        return (
          <div className="App">
            <Header onShowLogin={handleShowLogin} />
            <Hero onShowLogin={handleShowLogin} />
            <Features />
            <FreemiumFeatures />
            <OpportunitiesStatic />
            <Dashboard />
            <Footer />

            {/* Scroll-triggered login modal */}
            <ScrollTriggeredModal onLoginSuccess={handleLoginSuccess} />
          </div>
        );
    }
  };

  return renderCurrentPage();
}

export default App;
