import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useLanguage } from '../contexts/LanguageContext';

function DashboardHeader() {
  const { t, currentLanguage, changeLanguage } = useLanguage();
  const location = useLocation();

  const navItems = [
    { path: '/dashboard', label: 'Home', icon: '🏠' },
    { path: '/dashboard/opportunities', label: t('header.opportunities'), icon: '🎯' },
    { path: '/dashboard/mentors', label: t('header.mentors'), icon: '👥' },
    { path: '/dashboard/games', label: t('header.games'), icon: '🎮' },
    { path: '/dashboard/badges', label: t('header.badges'), icon: '🏆' },
    { path: '/dashboard/profile', label: t('header.profile'), icon: '👤' }
  ];

  const languages = [
    { code: 'en', label: t('language.english'), flag: '🇺🇸' },
    { code: 'hi', label: t('language.hindi'), flag: '🇮🇳' },
    { code: 'or', label: t('language.odia'), flag: '🇮🇳' }
  ];

  return (
    <header style={{
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      borderRadius: '0 0 20px 20px',
      padding: '1rem 2rem',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      margin: '0 2rem',
      marginBottom: '2rem'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        flexWrap: 'wrap',
        gap: '1rem'
      }}>
        {/* Logo */}
        <Link 
          to="/dashboard"
          style={{
            fontSize: '1.8rem',
            fontWeight: 'bold',
            color: 'white',
            textDecoration: 'none',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}
        >
          <span style={{
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            borderRadius: '10px',
            padding: '0.5rem',
            fontSize: '1.5rem'
          }}>
            🌉
          </span>
          {t('header.logo')}
        </Link>

        {/* Navigation */}
        <nav style={{
          display: 'flex',
          gap: '0.5rem',
          flexWrap: 'wrap'
        }}>
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '0.5rem 1rem',
                borderRadius: '15px',
                color: 'white',
                textDecoration: 'none',
                fontSize: '0.9rem',
                fontWeight: '500',
                background: location.pathname === item.path 
                  ? 'linear-gradient(45deg, #667eea, #764ba2)' 
                  : 'transparent',
                border: location.pathname === item.path 
                  ? '1px solid rgba(255, 255, 255, 0.3)' 
                  : '1px solid transparent',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                if (location.pathname !== item.path) {
                  e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                }
              }}
              onMouseLeave={(e) => {
                if (location.pathname !== item.path) {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              <span>{item.icon}</span>
              <span>{item.label}</span>
            </Link>
          ))}
        </nav>

        {/* Language Selector */}
        <div style={{
          position: 'relative',
          display: 'flex',
          alignItems: 'center',
          gap: '1rem'
        }}>
          <select
            value={currentLanguage}
            onChange={(e) => changeLanguage(e.target.value)}
            style={{
              background: 'rgba(255, 255, 255, 0.1)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              padding: '0.5rem',
              color: 'white',
              fontSize: '0.9rem',
              cursor: 'pointer'
            }}
          >
            {languages.map((lang) => (
              <option 
                key={lang.code} 
                value={lang.code}
                style={{ background: '#333', color: 'white' }}
              >
                {lang.flag} {lang.label}
              </option>
            ))}
          </select>
        </div>
      </div>
    </header>
  );
}

export default DashboardHeader;
