import React, { useState } from 'react';
import { useScrollAnimation } from '../../hooks/useScrollAnimation';
import './Features.css';

const Features = () => {
  const [ref, isVisible] = useScrollAnimation(0.2);
  const [activeFeature, setActiveFeature] = useState(null);

  const features = [
    {
      icon: 'fas fa-user-graduate',
      title: 'Student Profiling',
      description: 'Automated digital profiles using NLP and ML to analyze academic records, projects, skills, and extracurricular activities.',
      gradient: 'linear-gradient(135deg, #4cc9f0, #4361ee)',
      type: 'neon'
    },
    {
      icon: 'fas fa-chart-line',
      title: 'Career Recommendations',
      description: 'AI-powered clustering and predictive models for personalized career mapping and growth roadmaps.',
      gradient: 'linear-gradient(135deg, #4361ee, #3a0ca3)',
      type: 'holographic'
    },
    {
      icon: 'fas fa-handshake',
      title: 'Smart Matching',
      description: 'Advanced algorithms to match students with relevant internships and jobs based on skills and preferences.',
      gradient: 'linear-gradient(135deg, #3a0ca3, #f72585)',
      type: 'liquid'
    },
    {
      icon: 'fas fa-briefcase',
      title: 'Employer Dashboard',
      description: 'Comprehensive tools for posting opportunities, screening candidates, and managing recruitment pipelines.',
      gradient: 'linear-gradient(135deg, #f72585, #7209b7)',
      type: 'neon'
    },
    {
      icon: 'fas fa-chart-pie',
      title: 'Analytics & Insights',
      description: 'Data-driven dashboards showing placement trends, skill gaps, and industry demand forecasts.',
      gradient: 'linear-gradient(135deg, #7209b7, #4895ef)',
      type: 'frosted'
    },
    {
      icon: 'fas fa-award',
      title: 'Micro-Credentials',
      description: 'Gamified learning paths and verifiable digital badges to showcase your skills to employers.',
      gradient: 'linear-gradient(135deg, #4895ef, #4cc9f0)',
      type: 'holographic'
    }
  ];

  return (
    <section id="features" className="section section-gradient">
      <div className="section-title">
        <h2>How CareerConnect Works</h2>
        <p>Our platform leverages cutting-edge technology to transform career development and recruitment</p>
      </div>
      
      <div ref={ref} className="features-container">
        <div className="features-grid">
          {features.map((feature, index) => (
            <FeatureCard 
              key={index}
              {...feature}
              index={index}
              isVisible={isVisible}
              isActive={activeFeature === index}
              onHover={() => setActiveFeature(index)}
              onLeave={() => setActiveFeature(null)}
            />
          ))}
        </div>
        
        {/* Feature Spotlight */}
        <div className="feature-spotlight glass-frosted">
          {activeFeature !== null ? (
            <div className="spotlight-content">
              <div 
                className="spotlight-icon"
                style={{ background: features[activeFeature].gradient }}
              >
                <i className={features[activeFeature].icon}></i>
              </div>
              <h3>{features[activeFeature].title}</h3>
              <p>{features[activeFeature].description}</p>
              <div className="spotlight-glow"></div>
            </div>
          ) : (
            <div className="spotlight-placeholder">
              <i className="fas fa-mouse-pointer"></i>
              <p>Hover over a feature to learn more</p>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

const FeatureCard = ({ 
  icon, 
  title, 
  description, 
  gradient, 
  type, 
  index, 
  isVisible, 
  isActive, 
  onHover, 
  onLeave 
}) => (
  <div 
    className={`feature-card glass-${type} scroll-scale-in magnetic-hover ${
      isVisible ? 'visible' : ''
    } ${isActive ? 'active' : ''}`}
    style={{ 
      transitionDelay: `${index * 0.1}s`,
      '--tx': `${(index % 3 - 1) * 5}px`,
      '--ty': `${Math.floor(index / 3) * 5}px`
    }}
    onMouseEnter={onHover}
    onMouseLeave={onLeave}
  >
    <div 
      className="feature-icon-wrapper"
      style={{ background: gradient }}
    >
      <i className={icon}></i>
    </div>
    <h3 className="gradient-text">{title}</h3>
    <p>{description}</p>
    <div className="feature-hover-indicator">
      <div className="indicator-bar"></div>
    </div>
  </div>
);

export default Features;
