import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';

function Opportunities() {
  const { t } = useLanguage();
  const [activeCategory, setActiveCategory] = useState('all');

  const opportunities = [
    {
      id: 1,
      type: 'job',
      title: 'Senior Frontend Developer',
      company: 'Google',
      logo: '🔍',
      location: 'Bangalore, India',
      salary: '₹25-35 LPA',
      skills: ['React', 'TypeScript', 'Node.js'],
      deadline: '2024-11-15',
      applicants: '2.5K+'
    },
    {
      id: 2,
      type: 'internship',
      title: 'Software Development Intern',
      company: 'Amazon',
      logo: '📦',
      location: 'Bangalore, India',
      salary: '₹50K/month',
      skills: ['Java', 'AWS', 'Spring Boot'],
      deadline: '2024-11-10',
      applicants: '5K+'
    },
    {
      id: 3,
      type: 'hackathon',
      title: 'Smart India Hackathon 2024',
      company: 'Government of India',
      logo: '🇮🇳',
      location: 'Pan India',
      prize: '₹1 Lakh',
      skills: ['Innovation', 'Problem Solving'],
      deadline: '2024-12-01',
      applicants: '50K+'
    }
  ];

  const categories = [
    { id: 'all', label: t('opportunities.allOpportunities'), icon: '🌟' },
    { id: 'job', label: t('opportunities.jobs'), icon: '💼' },
    { id: 'internship', label: t('opportunities.internships'), icon: '🎯' },
    { id: 'hackathon', label: t('opportunities.hackathons'), icon: '💡' }
  ];

  const filteredOpportunities = activeCategory === 'all' 
    ? opportunities 
    : opportunities.filter(opp => opp.type === activeCategory);

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem',
      color: 'white'
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '20px',
          padding: '2rem',
          marginBottom: '2rem',
          textAlign: 'center'
        }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>
            {t('opportunities.title')}
          </h1>
          <p style={{ fontSize: '1.1rem', opacity: 0.9, marginBottom: '2rem' }}>
            {t('opportunities.subtitle')}
          </p>
          
          {/* Category Filters */}
          <div style={{
            display: 'flex',
            gap: '1rem',
            justifyContent: 'center',
            flexWrap: 'wrap'
          }}>
            {categories.map((category) => (
              <button
                key={category.id}
                onClick={() => setActiveCategory(category.id)}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  padding: '0.8rem 1.5rem',
                  borderRadius: '25px',
                  border: 'none',
                  background: activeCategory === category.id 
                    ? 'linear-gradient(45deg, #667eea, #764ba2)' 
                    : 'rgba(255, 255, 255, 0.1)',
                  color: 'white',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                <span>{category.icon}</span>
                <span>{category.label}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Stats */}
        <div style={{
          display: 'flex',
          gap: '2rem',
          justifyContent: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap'
        }}>
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            borderRadius: '15px',
            padding: '1rem 2rem',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#feca57' }}>
              {filteredOpportunities.length}
            </div>
            <div style={{ opacity: 0.8 }}>{t('opportunities.found')}</div>
          </div>
        </div>

        {/* Opportunities Grid */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '2rem'
        }}>
          {filteredOpportunities.map((opportunity) => (
            <div
              key={opportunity.id}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '20px',
                padding: '2rem',
                transition: 'transform 0.3s ease'
              }}
            >
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '1rem',
                marginBottom: '1rem'
              }}>
                <div style={{
                  fontSize: '2rem',
                  background: 'rgba(255, 255, 255, 0.1)',
                  borderRadius: '10px',
                  padding: '0.5rem',
                  width: '60px',
                  height: '60px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}>
                  {opportunity.logo}
                </div>
                <div>
                  <h3 style={{ margin: 0, fontSize: '1.3rem' }}>{opportunity.title}</h3>
                  <p style={{ margin: '0.25rem 0', opacity: 0.8 }}>{opportunity.company}</p>
                  <span style={{
                    background: 'linear-gradient(45deg, #48bb78, #38a169)',
                    color: 'white',
                    padding: '0.25rem 0.75rem',
                    borderRadius: '15px',
                    fontSize: '0.8rem',
                    fontWeight: '600'
                  }}>
                    {t(`opportunities.${opportunity.type}`)}
                  </span>
                </div>
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                  <span>📍</span>
                  <span>{opportunity.location}</span>
                </div>
                {opportunity.salary && (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                    <span>💰</span>
                    <span>{opportunity.salary}</span>
                  </div>
                )}
                {opportunity.prize && (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                    <span>🏆</span>
                    <span>{opportunity.prize}</span>
                  </div>
                )}
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <span>👥</span>
                  <span>{opportunity.applicants} applied</span>
                </div>
              </div>

              <div style={{ marginBottom: '1.5rem' }}>
                <h4 style={{ marginBottom: '0.5rem' }}>Required Skills:</h4>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
                  {opportunity.skills.map((skill, index) => (
                    <span
                      key={index}
                      style={{
                        background: 'rgba(255, 255, 255, 0.1)',
                        padding: '0.25rem 0.75rem',
                        borderRadius: '15px',
                        fontSize: '0.8rem'
                      }}
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              </div>

              <button style={{
                width: '100%',
                padding: '1rem',
                background: 'linear-gradient(45deg, #667eea, #764ba2)',
                color: 'white',
                border: 'none',
                borderRadius: '15px',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}>
                {t('opportunities.applyNow')}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Opportunities;
