// Memory Tab Component
const MemoryTab = ({ memoryData }) => (
  <div>
    <h4 style={{ margin: '0 0 16px 0', color: '#1f2937' }}>Memory Analysis</h4>
    
    {memoryData?.stats ? (
      <div>
        {/* Current Memory Status */}
        {memoryData.stats.current && (
          <div style={{ marginBottom: '20px' }}>
            <h5 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>Current Status</h5>
            <div style={{
              padding: '16px',
              backgroundColor: memoryData.stats.current.usagePercentage > 80 ? '#fef2f2' : '#f0fdf4',
              border: `1px solid ${memoryData.stats.current.usagePercentage > 80 ? '#ef4444' : '#22c55e'}`,
              borderRadius: '8px'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                <span style={{ fontSize: '14px', color: '#6b7280' }}>Usage:</span>
                <span style={{ 
                  fontSize: '16px', 
                  fontWeight: '600', 
                  color: memoryData.stats.current.usagePercentage > 80 ? '#ef4444' : '#22c55e'
                }}>
                  {Math.round(memoryData.stats.current.usagePercentage)}%
                </span>
              </div>
              <div style={{
                width: '100%',
                height: '8px',
                backgroundColor: '#e5e7eb',
                borderRadius: '4px',
                overflow: 'hidden'
              }}>
                <div style={{
                  width: `${memoryData.stats.current.usagePercentage}%`,
                  height: '100%',
                  backgroundColor: memoryData.stats.current.usagePercentage > 80 ? '#ef4444' : '#22c55e',
                  transition: 'width 0.3s ease'
                }}></div>
              </div>
            </div>
          </div>
        )}

        {/* Memory Trend */}
        {memoryData.stats.trend && (
          <div style={{ marginBottom: '20px' }}>
            <h5 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>Trend Analysis</h5>
            <div style={{
              padding: '16px',
              backgroundColor: '#f9fafb',
              border: '1px solid #e5e7eb',
              borderRadius: '8px'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '4px' }}>
                <span style={{ fontSize: '14px', color: '#6b7280' }}>Direction:</span>
                <span style={{ 
                  fontSize: '14px', 
                  fontWeight: '600', 
                  color: memoryData.stats.trend.isIncreasing ? '#ef4444' : '#22c55e'
                }}>
                  {memoryData.stats.trend.isIncreasing ? 'â†— Increasing' : 'â†˜ Stable/Decreasing'}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span style={{ fontSize: '14px', color: '#6b7280' }}>Rate:</span>
                <span style={{ fontSize: '14px', fontWeight: '600', color: '#1f2937' }}>
                  {Math.round(memoryData.stats.trend.rate / 1024)} KB/s
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Recommendations */}
        {memoryData.recommendations && memoryData.recommendations.length > 0 && (
          <div>
            <h5 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>Recommendations</h5>
            {memoryData.recommendations.map((rec, index) => (
              <div key={index} style={{
                padding: '12px',
                backgroundColor: rec.priority === 'critical' ? '#fef2f2' : rec.priority === 'high' ? '#fef3c7' : '#f0fdf4',
                border: `1px solid ${rec.priority === 'critical' ? '#ef4444' : rec.priority === 'high' ? '#f59e0b' : '#22c55e'}`,
                borderRadius: '6px',
                marginBottom: '8px'
              }}>
                <div style={{ fontSize: '14px', color: '#1f2937' }}>{rec.message}</div>
                <div style={{ fontSize: '12px', color: '#6b7280', marginTop: '4px' }}>
                  Priority: {rec.priority}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    ) : (
      <div style={{ textAlign: 'center', color: '#6b7280', padding: '40px' }}>
        No memory data available yet
      </div>
    )}
  </div>
);

// Optimization Tab Component
