          </div>
        </div>
      </div>
    )}

    {/* Recommendations */}
    {performanceData?.optimizer?.recommendations && performanceData.optimizer.recommendations.length > 0 && (
      <div>
        <h5 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>Recommendations</h5>
        {performanceData.optimizer.recommendations.map((rec, index) => (
          <div key={index} style={{
            padding: '12px',
            backgroundColor: rec.priority === 'high' ? '#fef3c7' : '#f0fdf4',
            border: `1px solid ${rec.priority === 'high' ? '#f59e0b' : '#22c55e'}`,
            borderRadius: '6px',
            marginBottom: '8px'
          }}>
            <div style={{ fontSize: '14px', color: '#1f2937' }}>{rec.message}</div>
            <div style={{ fontSize: '12px', color: '#6b7280', marginTop: '4px' }}>
              Priority: {rec.priority} | Type: {rec.type}
            </div>
          </div>
        ))}
      </div>
    )}
  </div>
);

export default PerformanceDashboard;
