import React, { useState, useEffect } from 'react';
import { useScrollAnimation } from '../../hooks/useScrollAnimation';
import ApiService from '../../services/api';
import './Opportunities.css';

const Opportunities = () => {
  const [ref, isVisible] = useScrollAnimation(0.2);
  const [activeFilter, setActiveFilter] = useState('all');
  const [opportunities, setOpportunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch opportunities from API
  useEffect(() => {
    const fetchOpportunities = async () => {
      try {
        setLoading(true);
        const data = await ApiService.getOpportunities({ limit: 20 });
        
        // Transform API data to match component structure
        const transformedOpportunities = data.opportunities.map((opp, index) => {
          // Create company logo from company name
          const getCompanyLogo = (companyName) => {
            const logoMap = {
              'TechCorp': 'TC',
              'DataAnalytics Inc': 'DA', 
              'MarketGuru': 'MG',
              'CloudTech Solutions': 'CS',
              'FinanceFlow': 'FF',
              'GameStudio Pro': 'GP',
              'EcoTech Innovations': 'ET'
            };
            return logoMap[companyName] || companyName.substring(0, 2).toUpperCase();
          };

          // Format salary based on type
          const formatSalary = (type, salaryMin) => {
            if (type === 'internship') {
              return `$${(salaryMin / 1000).toFixed(1)}k/mo`;
            } else {
              return `$${(salaryMin / 1000)}k/yr`;
            }
          };

          // Format location
          const formatLocation = (location) => {
            if (location?.remote) return 'Remote';
            if (location?.state) return `${location.city}, ${location.state}`;
            return location?.city || 'Remote';
          };

          // Determine if urgent (deadline within 7 days)
          const isUrgent = opp.application_deadline 
            ? new Date(opp.application_deadline) < new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)
            : false;

          // Featured opportunities (based on company or random)
          const featuredCompanies = ['TechCorp', 'DataAnalytics Inc', 'CloudTech Solutions', 'FinanceFlow'];
          const isFeatured = featuredCompanies.includes(opp.company_name);

          return {
            id: opp.id,
            company: opp.company_name || 'Company Name',
            industry: opp.industry_name || 'Technology',
            logo: getCompanyLogo(opp.company_name),
            type: opp.opportunity_type,
            title: opp.title,
            location: formatLocation(opp.location),
            salary: formatSalary(opp.opportunity_type, opp.salary_min || 50000),
            skills: ['React', 'JavaScript', 'Python'], // Will be enhanced with real skills later
            deadline: opp.application_deadline 
              ? new Date(opp.application_deadline).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })
              : 'Open',
            urgent: isUrgent,
            featured: isFeatured,
            gradient: this.getGradientForCompany(opp.company_name, index)
          };
        });

        // Add gradient mapping method
        const getGradientForCompany = (companyName, index) => {
          const gradientMap = {
            'TechCorp': 'linear-gradient(135deg, #4cc9f0, #4361ee)',
            'DataAnalytics Inc': 'linear-gradient(135deg, #4361ee, #3a0ca3)',
            'MarketGuru': 'linear-gradient(135deg, #f72585, #7209b7)',
            'CloudTech Solutions': 'linear-gradient(135deg, #06ffa5, #4cc9f0)',
            'FinanceFlow': 'linear-gradient(135deg, #7209b7, #f72585)',
            'GameStudio Pro': 'linear-gradient(135deg, #ff6b6b, #feca57)',
            'EcoTech Innovations': 'linear-gradient(135deg, #48cae4, #06ffa5)'
          };
          return gradientMap[companyName] || `linear-gradient(135deg, hsl(${index * 60}, 70%, 60%), hsl(${(index * 60) + 30}, 70%, 50%))`;
        };

        // Apply gradient mapping to transformed opportunities
        const finalOpportunities = transformedOpportunities.map((opp, index) => ({
          ...opp,
          gradient: getGradientForCompany(opp.company, index)
        }));
        
        setOpportunities(transformedOpportunities);
        setError(null);
