import React, { useState, useEffect } from 'react';

const SimpleApiTest = () => {
  const [status, setStatus] = useState('Testing...');
  const [data, setData] = useState(null);

  useEffect(() => {
    const testApi = async () => {
      try {
        console.log('üß™ SimpleApiTest: Starting API test...');
        
        const response = await fetch('http://localhost:3001/api/opportunities?limit=3');
        console.log('üß™ SimpleApiTest: Response status:', response.status);
        
        if (!response.ok) {
          throw new Error(`HTTP ${response.status}`);
        }
        
        const result = await response.json();
        console.log('üß™ SimpleApiTest: Data received:', result);
        
        setStatus('‚úÖ API Working!');
        setData(result);
        
      } catch (error) {
        console.error('üß™ SimpleApiTest: Error:', error);
        setStatus(`‚ùå API Failed: ${error.message}`);
      }
    };

    testApi();
  }, []);

  return (
    <div style={{
      position: 'fixed',
      top: '10px',
      left: '10px',
      background: 'white',
      padding: '15px',
      border: '2px solid #4cc9f0',
      borderRadius: '8px',
      zIndex: 9999,
      fontSize: '14px',
      maxWidth: '300px'
    }}>
      <h4 style={{ margin: '0 0 10px 0' }}>API Test</h4>
      <p style={{ margin: '0 0 10px 0' }}>{status}</p>
      {data && (
        <div>
          <p><strong>Opportunities:</strong> {data.opportunities?.length || 0}</p>
          {data.opportunities?.[0] && (
            <p><strong>First:</strong> {data.opportunities[0].title}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default SimpleApiTest;
