import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';

function Badges() {
  const { t } = useLanguage();
  const [activeTab, setActiveTab] = useState('all');

  const userStats = {
    totalBadges: 15,
    rareBadges: 8,
    epicBadges: 3,
    legendaryBadges: 2
  };

  const badges = [
    {
      id: 1,
      name: 'Daily Warrior',
      description: 'Login for 7 consecutive days',
      icon: '🗡️',
      rarity: 'common',
      earned: true,
      earnedDate: '2024-10-15',
      xp: 100,
      background: 'linear-gradient(135deg, #4ade80 0%, #22c55e 100%)'
    },
    {
      id: 2,
      name: 'Streak Master',
      description: 'Maintain a 30-day login streak',
      icon: '🔥',
      rarity: 'rare',
      earned: true,
      earnedDate: '2024-10-10',
      xp: 500,
      background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)'
    },
    {
      id: 3,
      name: 'Game Champion',
      description: 'Win 25 games across all platforms',
      icon: '🏆',
      rarity: 'epic',
      earned: true,
      earnedDate: '2024-10-08',
      xp: 750,
      background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)'
    },
    {
      id: 4,
      name: 'Code Ninja',
      description: 'Solve 100 coding problems',
      icon: '🥷',
      rarity: 'epic',
      earned: false,
      progress: 73,
      maxProgress: 100,
      xp: 1000,
      background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)'
    },
    {
      id: 5,
      name: 'BriDGe Elite',
      description: 'Exclusive Pro member badge',
      icon: '👑',
      rarity: 'legendary',
      earned: true,
      earnedDate: '2024-10-01',
      xp: 2000,
      background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 50%, #d97706 100%)',
      isPro: true
    },
    {
      id: 6,
      name: 'Learning Enthusiast',
      description: 'Complete 10 courses',
      icon: '📚',
      rarity: 'common',
      earned: true,
      earnedDate: '2024-10-14',
      xp: 300,
      background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)'
    }
  ];

  const tabs = [
    { id: 'all', label: t('badges.allBadges'), icon: '🌟' },
    { id: 'earned', label: t('badges.earned'), icon: '✅' },
    { id: 'locked', label: t('badges.locked'), icon: '🔒' }
  ];

  const filteredBadges = () => {
    switch(activeTab) {
      case 'earned':
        return badges.filter(badge => badge.earned);
      case 'locked':
        return badges.filter(badge => !badge.earned);
      default:
        return badges;
    }
  };

  const getRarityColor = (rarity) => {
    switch(rarity) {
      case 'common': return '#10b981';
      case 'rare': return '#3b82f6';
      case 'epic': return '#8b5cf6';
      case 'legendary': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem',
      color: 'white'
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '20px',
          padding: '2rem',
          marginBottom: '2rem',
          textAlign: 'center'
        }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>
            {t('badges.title')}
          </h1>
          <p style={{ fontSize: '1.1rem', opacity: 0.9, marginBottom: '2rem' }}>
            {t('badges.subtitle')}
          </p>

          {/* Stats */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
            gap: '1rem',
            maxWidth: '600px',
            margin: '0 auto'
          }}>
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '15px',
              padding: '1rem'
            }}>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#feca57' }}>
                {userStats.totalBadges}
              </div>
              <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>{t('badges.totalBadges')}</div>
            </div>
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '15px',
              padding: '1rem'
            }}>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#3b82f6' }}>
                {userStats.rareBadges}
              </div>
              <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>{t('badges.rareBadges')}</div>
            </div>
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '15px',
              padding: '1rem'
            }}>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#8b5cf6' }}>
                {userStats.epicBadges}
              </div>
              <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>{t('badges.epicBadges')}</div>
            </div>
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '15px',
              padding: '1rem'
            }}>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#f59e0b' }}>
                {userStats.legendaryBadges}
              </div>
              <div style={{ fontSize: '0.8rem', opacity: 0.8 }}>{t('badges.legendaryBadges')}</div>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div style={{
          display: 'flex',
          gap: '1rem',
          justifyContent: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap'
        }}>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '0.8rem 1.5rem',
                borderRadius: '25px',
                border: 'none',
                background: activeTab === tab.id 
                  ? 'linear-gradient(45deg, #667eea, #764ba2)' 
                  : 'rgba(255, 255, 255, 0.1)',
                color: 'white',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              <span>{tab.icon}</span>
              <span>{tab.label}</span>
            </button>
          ))}
        </div>

        {/* Badges Grid */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '2rem'
        }}>
          {filteredBadges().map((badge) => (
            <div
              key={badge.id}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)',
                border: `2px solid ${getRarityColor(badge.rarity)}`,
                borderRadius: '20px',
                padding: '2rem',
                textAlign: 'center',
                opacity: badge.earned ? 1 : 0.7,
                transition: 'transform 0.3s ease',
                position: 'relative',
                overflow: 'hidden'
              }}
            >
              {/* Rarity indicator */}
              <div style={{
                position: 'absolute',
                top: '1rem',
                right: '1rem',
                background: getRarityColor(badge.rarity),
                color: 'white',
                padding: '0.25rem 0.75rem',
                borderRadius: '15px',
                fontSize: '0.7rem',
                fontWeight: 'bold',
                textTransform: 'uppercase'
              }}>
                {badge.rarity}
              </div>

              {/* Pro badge indicator */}
              {badge.isPro && (
                <div style={{
                  position: 'absolute',
                  top: '1rem',
                  left: '1rem',
                  background: 'linear-gradient(45deg, #fbbf24, #f59e0b)',
                  color: 'white',
                  padding: '0.25rem 0.75rem',
                  borderRadius: '15px',
                  fontSize: '0.7rem',
                  fontWeight: 'bold'
                }}>
                  PRO
                </div>
              )}

              {/* Badge Icon */}
              <div style={{
                fontSize: '4rem',
                marginBottom: '1rem',
                background: badge.background,
                borderRadius: '50%',
                width: '100px',
                height: '100px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                margin: '0 auto 1rem',
                boxShadow: `0 0 30px ${getRarityColor(badge.rarity)}40`
              }}>
                {badge.icon}
              </div>

              <h3 style={{ margin: '0 0 0.5rem 0', fontSize: '1.3rem' }}>{badge.name}</h3>
              <p style={{ margin: '0 0 1rem 0', opacity: 0.8, fontSize: '0.9rem' }}>
                {badge.description}
              </p>

              {/* Progress bar for locked badges */}
              {!badge.earned && badge.progress && (
                <div style={{ marginBottom: '1rem' }}>
                  <div style={{
                    background: 'rgba(255, 255, 255, 0.1)',
                    borderRadius: '10px',
                    height: '8px',
                    overflow: 'hidden'
                  }}>
                    <div style={{
                      background: getRarityColor(badge.rarity),
                      height: '100%',
                      width: `${(badge.progress / badge.maxProgress) * 100}%`,
                      transition: 'width 0.3s ease'
                    }} />
                  </div>
                  <div style={{ fontSize: '0.8rem', marginTop: '0.5rem', opacity: 0.8 }}>
                    {badge.progress} / {badge.maxProgress}
                  </div>
                </div>
              )}

              {/* XP Reward */}
              <div style={{
                background: 'rgba(255, 255, 255, 0.1)',
                borderRadius: '10px',
                padding: '0.5rem',
                fontSize: '0.9rem',
                fontWeight: '600'
              }}>
                🌟 {badge.xp} XP
              </div>

              {/* Earned date */}
              {badge.earned && badge.earnedDate && (
                <div style={{
                  marginTop: '1rem',
                  fontSize: '0.8rem',
                  opacity: 0.7
                }}>
                  {t('badges.earnedOn')} {new Date(badge.earnedDate).toLocaleDateString()}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Badges;
