import { useEffect, useRef, useState } from 'react';

// Hook: useScrollAnimation
// Returns a ref and a boolean indicating whether the element is visible in the viewport.
// threshold: IntersectionObserver threshold (default 0.1)
export const useScrollAnimation = (threshold = 0.1) => {
  const ref = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const node = ref.current;
    if (!node || typeof IntersectionObserver === 'undefined') return;

    const observer = new IntersectionObserver(
      (entries) => {
        const entry = entries[0];
        setIsVisible(entry.isIntersecting);
      },
      { threshold }
    );

    observer.observe(node);

    return () => observer.disconnect();
  }, [threshold]);

  return [ref, isVisible];
};

export default useScrollAnimation;

// Hook: useScrollDirection
// Returns 'up', 'down', or 'none' depending on window scroll direction
export const useScrollDirection = (threshold = 50) => {
  const [direction, setDirection] = useState('none');
  useEffect(() => {
    let lastScroll = window.pageYOffset || document.documentElement.scrollTop;
    let ticking = false;

    const update = () => {
      const current = window.pageYOffset || document.documentElement.scrollTop;
      const diff = current - lastScroll;
      if (Math.abs(diff) < threshold) {
        // ignore small movements
        ticking = false;
        return;
      }
      setDirection(diff > 0 ? 'down' : 'up');
      lastScroll = current > 0 ? current : 0;
      ticking = false;
    };

    const onScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(update);
        ticking = true;
      }
    };

    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, [threshold]);

  return direction;
};
