import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { LanguageProvider } from './contexts/LanguageContext';

// Landing page components
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import FreemiumFeatures from './components/FreemiumFeatures';
import OpportunitiesStatic from './components/Opportunities/OpportunitiesStatic';
import Dashboard from './components/Dashboard';
import Footer from './components/Footer';
import LoginPage from './components/LoginPage';
import ProfileCompletionForm from './components/ProfileCompletionForm';
import StudentForm from './components/StudentForm';
import ScrollTriggeredModal from './components/ScrollTriggeredModal';

// Dashboard components
import DashboardHeader from './components/DashboardHeader';
import DashboardHome from './pages/DashboardHome';
import Profile from './pages/Profile';
import Opportunities from './pages/Opportunities';
import Mentors from './pages/Mentors';
import Games from './pages/Games';
import Badges from './pages/Badges';

import './styles/globals.css';
import './styles/dashboard.css';
import './App.css';

// Landing Page Component
function LandingPage() {
  const [currentPage, setCurrentPage] = useState('home');
  const [userData, setUserData] = useState(null);

  const handleLoginSuccess = (data) => {
    setUserData(data);
    setCurrentPage('profile');
  };

  const handleProfileComplete = (profileData) => {
    const updatedUserData = { ...userData, ...profileData };
    setUserData(updatedUserData);

    // Check if user clicked skip
    if (profileData.skipped) {
      // Redirect to dashboard
      window.location.href = '/dashboard';
      return;
    }

    // Navigate to appropriate form based on user role
    if (userData.userType === 'student') {
      setCurrentPage('studentForm');
    } else if (userData.userType === 'employer') {
      window.location.href = '/dashboard';
    } else if (userData.userType === 'institution') {
      window.location.href = '/dashboard';
    }
  };

  const handleBackToProfile = () => {
    setCurrentPage('profile');
  };

  const handleBackToLogin = () => {
    setCurrentPage('login');
    setUserData(null);
  };

  const handleBackToHome = () => {
    setCurrentPage('home');
  };

  const handleShowLogin = () => {
    setCurrentPage('login');
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'login':
        return <LoginPage onLoginSuccess={handleLoginSuccess} onClose={handleBackToHome} />;

      case 'profile':
        return (
          <ProfileCompletionForm
            userRole={userData?.userType}
            initialData={userData}
            onSubmit={handleProfileComplete}
            onBack={handleBackToLogin}
          />
        );

      case 'studentForm':
        return (
          <StudentForm
            studentData={userData}
            onBack={handleBackToProfile}
          />
        );

      case 'home':
      default:
        return (
          <div className="App">
            <Header onShowLogin={handleShowLogin} />
            <Hero onShowLogin={handleShowLogin} />
            <Features />
            <FreemiumFeatures />
            <OpportunitiesStatic />
            <Dashboard />
            <Footer />

            {/* Scroll-triggered login modal */}
            <ScrollTriggeredModal onLoginSuccess={handleLoginSuccess} />
          </div>
        );
    }
  };

  return renderCurrentPage();
}

// Dashboard Layout Component
function DashboardLayout({ children }) {
  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <DashboardHeader />
      {children}
    </div>
  );
}

function App() {
  return (
    <LanguageProvider>
      <Router>
        <Routes>
          {/* Landing page routes */}
          <Route path="/" element={<LandingPage />} />

          {/* Dashboard routes */}
          <Route path="/dashboard" element={
            <DashboardLayout>
              <DashboardHome />
            </DashboardLayout>
          } />
          <Route path="/dashboard/profile" element={
            <DashboardLayout>
              <Profile />
            </DashboardLayout>
          } />
          <Route path="/dashboard/opportunities" element={
            <DashboardLayout>
              <Opportunities />
            </DashboardLayout>
          } />
          <Route path="/dashboard/mentors" element={
            <DashboardLayout>
              <Mentors />
            </DashboardLayout>
          } />
          <Route path="/dashboard/games" element={
            <DashboardLayout>
              <Games />
            </DashboardLayout>
          } />
          <Route path="/dashboard/badges" element={
            <DashboardLayout>
              <Badges />
            </DashboardLayout>
          } />

          {/* Redirect any unknown routes to landing page */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Router>
    </LanguageProvider>
  );
}

export default App;
