import React, { useState, useEffect } from 'react';
import ApiService from '../services/api';

const ApiTest = () => {
  const [status, setStatus] = useState('Testing...');
  const [results, setResults] = useState([]);

  useEffect(() => {
    testApiConnection();
  }, []);

  const testApiConnection = async () => {
    const testResults = [];
    
    try {
      // Test 1: Health check
      setStatus('Testing health endpoint...');
      const health = await ApiService.healthCheck();
      testResults.push({
        test: 'Health Check',
        status: '✅ Success',
        data: `Status: ${health.status}, DB: ${health.database}`
      });

      // Test 2: Opportunities
      setStatus('Testing opportunities endpoint...');
      const opportunities = await ApiService.getOpportunities({ limit: 1 });
      testResults.push({
        test: 'Opportunities API',
        status: '✅ Success',
        data: `Found ${opportunities.opportunities.length} opportunities`
      });

      setStatus('✅ All tests passed!');
      
    } catch (error) {
      testResults.push({
        test: 'API Connection',
        status: '❌ Failed',
        data: error.message
      });
      setStatus('❌ Tests failed');
    }
    
    setResults(testResults);
  };

  return (
    <div style={{ 
      position: 'fixed', 
      top: '10px', 
      right: '10px', 
      background: 'white', 
      padding: '20px', 
      border: '2px solid #4cc9f0',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      zIndex: 9999,
      maxWidth: '300px'
    }}>
      <h3 style={{ margin: '0 0 10px 0', color: '#4361ee' }}>API Connection Test</h3>
      <p style={{ margin: '0 0 10px 0', fontSize: '14px' }}>{status}</p>
      
      {results.map((result, index) => (
        <div key={index} style={{ 
          marginBottom: '10px', 
          padding: '8px', 
          background: '#f8f9fa',
          borderRadius: '5px',
          fontSize: '12px'
        }}>
          <strong>{result.test}:</strong> {result.status}<br/>
          <span style={{ color: '#666' }}>{result.data}</span>
        </div>
      ))}
      
      <button 
        onClick={testApiConnection}
        style={{
          background: '#4cc9f0',
          color: 'white',
          border: 'none',
          padding: '8px 16px',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '12px'
        }}
      >
        Retest API
      </button>
    </div>
  );
};

export default ApiTest;
