import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Bridge components (pre-login)
import BridgeHeader from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import FreemiumFeatures from './components/FreemiumFeatures';
import OpportunitiesStatic from './components/Opportunities/OpportunitiesStatic';
import Dashboard from './components/Dashboard';
import Footer from './components/Footer';
import LoginPage from './components/LoginPage';
import ProfileCompletionForm from './components/ProfileCompletionForm';
import StudentForm from './components/StudentForm';
import ScrollTriggeredModal from './components/ScrollTriggeredModal';

// Student components (post-login)
import StudentHeader from './components/student/Header';
import StudentHome from './pages/Home';
import Profile from './pages/Profile';
import ProfileEdit from './pages/ProfileEdit';
import Games from './pages/Games';
import Opportunities from './pages/Opportunities';
import Mentors from './pages/Mentors';
import Badges from './pages/Badges';
import AIPro from './pages/AIPro';

// Contexts and styles
import { LanguageProvider } from './contexts/LanguageContext';
import './styles/globals.css';
import './styles/student/styles.css';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'login', 'profile', 'studentForm', 'dashboard'
  const [userData, setUserData] = useState(null);

  const handleLoginSuccess = (data) => {
    setUserData(data);
    setCurrentPage('profile');
  };

  const handleProfileComplete = (profileData) => {
    const updatedUserData = { ...userData, ...profileData };
    setUserData(updatedUserData);

    // Navigate to appropriate form based on user role
    if (userData.userType === 'student') {
      setCurrentPage('studentForm');
    } else if (userData.userType === 'employer') {
      setCurrentPage('home'); // For now, redirect employers back to home
    } else if (userData.userType === 'institution') {
      setCurrentPage('home'); // For now, redirect institutions back to home
    }
  };

  const handleStudentFormComplete = () => {
    setIsLoggedIn(true);
    setCurrentPage('dashboard');
  };

  const handleStudentFormSkip = () => {
    setIsLoggedIn(true);
    setCurrentPage('dashboard');
  };

  const handleBackToProfile = () => {
    setCurrentPage('profile');
  };

  const handleBackToLogin = () => {
    setCurrentPage('login');
    setUserData(null);
  };

  const handleBackToHome = () => {
    setCurrentPage('home');
  };

  const handleShowLogin = () => {
    setCurrentPage('login');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setCurrentPage('home');
    setUserData(null);
  };

  // Pre-login pages
  const renderPreLoginPage = () => {
    switch (currentPage) {
      case 'login':
        return <LoginPage onLoginSuccess={handleLoginSuccess} onClose={handleBackToHome} />;

      case 'profile':
        return (
          <ProfileCompletionForm
            userRole={userData?.userType}
            initialData={userData}
            onSubmit={handleProfileComplete}
            onBack={handleBackToLogin}
          />
        );

      case 'studentForm':
        return (
          <StudentForm
            studentData={userData}
            onBack={handleBackToProfile}
            onComplete={handleStudentFormComplete}
            onSkip={handleStudentFormSkip}
          />
        );

      case 'home':
      default:
        return (
          <div className="App">
            <BridgeHeader onShowLogin={handleShowLogin} />
            <Hero onShowLogin={handleShowLogin} />
            <Features />
            <FreemiumFeatures />
            <OpportunitiesStatic />
            <Dashboard />
            <Footer />

            {/* Scroll-triggered login modal */}
            <ScrollTriggeredModal onLoginSuccess={handleLoginSuccess} />
          </div>
        );
    }
  };

  // Post-login student dashboard
  const renderStudentDashboard = () => {
    return (
      <LanguageProvider>
        <Router>
          <div className="App">
            <StudentHeader onLogout={handleLogout} />
            <Routes>
              <Route path="/" element={<StudentHome />} />
              <Route path="/opportunities" element={<Opportunities />} />
              <Route path="/mentors" element={<Mentors />} />
              <Route path="/badges" element={<Badges />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/profile/edit" element={<ProfileEdit />} />
              <Route path="/games" element={<Games />} />
              <Route path="/aipro" element={<AIPro />} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </div>
        </Router>
      </LanguageProvider>
    );
  };

  return isLoggedIn ? renderStudentDashboard() : renderPreLoginPage();
}

export default App;
